import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import authService from '../../services/authService';

const OTPVerification = () => {
  const [otp, setOtp] = useState(['', '', '', '', '', '']);
  const [loading, setLoading] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [canResend, setCanResend] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { phone, isNewPartner, isApproved } = location.state || {};

  const otpInputs = useRef([]);

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else {
      setCanResend(true);
    }
  }, [timeLeft]);

  const handleOtpChange = (index, value) => {
    if (value.length > 1) return;

    const newOtp = [...otp];
    newOtp[index] = value;
    setOtp(newOtp);

    // Auto-focus next input
    if (value && index < 5) {
      otpInputs.current[index + 1]?.focus();
    }
  };

  const handleKeyDown = (index, e) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      otpInputs.current[index - 1]?.focus();
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    const otpString = otp.join('');

    if (otpString.length !== 6) {
      alert('Please enter a valid 6-digit OTP');
      return;
    }

    try {
      setLoading(true);
      console.log('üîç Starting OTP verification for:', phone);

      const response = await authService.verifyOTP(phone, otpString);
      console.log('üì¶ Received response:', response);

      if (response.success && response.token) {
        console.log('‚úÖ OTP verified successfully!');
        console.log('üíæ Storing token...');

        // Store token explicitly
        localStorage.setItem('partnerToken', response.token);
        localStorage.setItem('phoneNumber', phone);

        // Verify token was stored
        const storedToken = localStorage.getItem('partnerToken');
        console.log('üîç Token stored?', storedToken ? 'YES' : 'NO');

        if (!storedToken) {
          console.error('‚ùå Token storage failed!');
          alert('Login failed: Unable to store authentication token. Please check browser settings.');
          return;
        }

        console.log('‚úÖ Token stored successfully!');
        console.log('üîÑ Redirecting to dashboard...');

        // Small delay to ensure localStorage is persisted
        setTimeout(() => {
          window.location.replace('/');
        }, 100);

      } else {
        console.warn('‚ö†Ô∏è Verification failed:', response);
        alert(response.message || 'OTP verification failed. Please try again.');
      }
    } catch (error) {
      console.error('‚ùå Error during OTP verification:', error);
      alert(error.response?.data?.message || 'Invalid OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleResendOTP = async () => {
    try {
      setLoading(true);
      await authService.sendOTP(phone);
      setTimeLeft(60);
      setCanResend(false);
      setOtp(['', '', '', '', '', '']);
      otpInputs.current[0]?.focus();
      alert('OTP sent successfully');
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to resend OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
        <div className="text-center mb-8">
          <h1 className="text-2xl font-bold text-gray-900">Verify OTP</h1>
          <p className="text-gray-600 mt-2">Enter the 6-digit code sent to {phone}</p>
        </div>

        <form onSubmit={handleVerifyOTP} className="space-y-6">
          <div className="flex justify-center space-x-2">
            {otp.map((digit, index) => (
              <input
                key={index}
                ref={(el) => (otpInputs.current[index] = el)}
                type="text"
                value={digit}
                onChange={(e) => handleOtpChange(index, e.target.value)}
                onKeyDown={(e) => handleKeyDown(index, e)}
                className="w-12 h-12 text-center text-lg font-semibold border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none"
                maxLength={1}
                required
              />
            ))}
          </div>

          <button
            type="submit"
            disabled={loading || otp.join('').length !== 6}
            className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-blue-700 transition duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Verifying...' : 'Verify OTP'}
          </button>

          <div className="text-center">
            <button
              type="button"
              onClick={handleResendOTP}
              disabled={!canResend || loading}
              className="text-blue-600 hover:text-blue-700 text-sm disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {canResend ? 'Resend OTP' : `Resend OTP in ${timeLeft}s`}
            </button>
          </div>
        </form>

        <div className="mt-6 text-center">
          <button
            onClick={() => navigate('/')}
            className="text-gray-600 hover:text-gray-700 text-sm"
          >
            ‚Üê Back to Mobile Number
          </button>
        </div>
      </div>
    </div>
  );
};

export default OTPVerification;
