import React, { useState, useEffect, useRef } from 'react';
import { Tab } from '@headlessui/react';
import {
  UserGroupIcon,
  ClockIcon,
  CheckCircleIcon,
  CurrencyDollarIcon,
  ExclamationCircleIcon
} from '@heroicons/react/24/outline';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

import partnerService from '../../services/partnerService';
import BookingList from './BookingList';
// BookingCard import removed as it is used inside BookingList
import webSocketService from '../../services/websocket';
import Navbar from '../common/Navbar';

// Fix for default marker icon in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

// Component to recenter map when coordinates change
function ChangeView({ center, zoom }) {
  const map = useMap();
  useEffect(() => {
    map.setView(center, zoom);
  }, [center, zoom, map]);
  return null;
}

const Dashboard = () => {
  const [availableBookings, setAvailableBookings] = useState([]);
  const [myBookings, setMyBookings] = useState([]);
  const [stats, setStats] = useState({
    totalBookings: 0,
    completedBookings: 0,
    pendingBookings: 0,
    todayEarnings: 0,
    totalEarnings: 0,
    averageRating: 0
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load initial data
  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);
        const [statsData, availableData, myBookingsData] = await Promise.all([
          partnerService.getStats().catch(() => ({})),
          partnerService.getAvailableBookings().catch(() => ({ data: [] })),
          partnerService.getBookings().catch(() => ({ bookings: [] }))
        ]);

        setStats(prev => ({ ...prev, ...statsData }));
        setAvailableBookings(availableData.data || []);
        // Filter out pending AND accepted bookings from "My Bookings" as they are shown in "Available"
        const myBookingsList = (myBookingsData.bookings || []).filter(b =>
          b && !['pending', 'accepted'].includes(b.status)
        );
        setMyBookings(myBookingsList);
      } catch (err) {
        setError('Failed to load dashboard data');
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, []);

  // Setup WebSocket subscription
  useEffect(() => {
    const unsubscribe = partnerService.subscribeToBookings((event) => {
      console.log('WebSocket event received:', event);

      switch (event.type) {
        case 'INITIAL_DATA':
          console.log('INITIAL_DATA received:', {
            available: event.available?.length || 0,
            accepted: event.accepted?.length || 0
          });
          setAvailableBookings(event.available || []);
          setMyBookings(event.accepted || []);
          break;
        case 'NEW_BOOKING':
          console.log('NEW_BOOKING received:', event.booking);
          if (event.booking) {
            setAvailableBookings(prev => [event.booking, ...prev]);
          }
          break;
        case 'BOOKING_UPDATED':
          console.log('BOOKING_UPDATED received:', event.booking);
          if (!event.booking || !event.booking._id) break;

          // Remove from available if it was there
          setAvailableBookings(prev =>
            (prev || []).filter(b => b && b._id !== event.booking._id)
          );
          // Update in my bookings if it's there, or add if it's now assigned to me
          setMyBookings(prev => {
            const current = prev || [];
            const exists = current.some(b => b && b._id === event.booking._id);
            if (exists) {
              return current.map(b =>
                (b && b._id === event.booking._id) ? event.booking : b
              );
            } else if (['accepted', 'in-progress'].includes(event.booking.status)) {
              return [event.booking, ...current];
            }
            return current;
          });
          break;
        case 'BOOKING_CANCELLED':
          console.log('BOOKING_CANCELLED received:', event.bookingId);
          if (!event.bookingId) break;

          setAvailableBookings(prev =>
            (prev || []).filter(b => b && b._id !== event.bookingId)
          );
          setMyBookings(prev =>
            (prev || []).filter(b => b && b._id !== event.bookingId)
          );
          break;
        default:
          console.log('Unknown event type:', event.type);
      }
    });

    return () => {
      if (unsubscribe) unsubscribe();
    };
  }, []);

  // --- Map & Partner Location Logic ---
  const [partnerLocation, setPartnerLocation] = useState(null); // { lat, lng }
  const watchIdRef = useRef(null);

  useEffect(() => {
    if ('geolocation' in navigator) {
      watchIdRef.current = navigator.geolocation.watchPosition(
        (position) => {
          setPartnerLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (err) => console.error('Error getting location:', err),
        { enableHighAccuracy: true, maximumAge: 10000, timeout: 5000 }
      );
    }
    return () => {
      if (watchIdRef.current !== null) {
        navigator.geolocation.clearWatch(watchIdRef.current);
      }
    };
  }, []);

  const handleAcceptBooking = async (bookingId) => {
    try {
      await partnerService.updateBookingStatus(bookingId, 'accepted');
    } catch (error) {
      console.error('Error accepting booking:', error);
      setError('Failed to accept booking. Please try again.');
    }
  };

  const handleRejectBooking = async (bookingId) => {
    try {
      await partnerService.updateBookingStatus(bookingId, 'rejected', 'Partner rejected');
    } catch (error) {
      console.error('Error rejecting booking:', error);
      setError('Failed to reject booking. Please try again.');
    }
  };

  const handleStatusUpdate = async (bookingId, status) => {
    try {
      await partnerService.updateBookingStatus(bookingId, status);
      // Update local state is handled by WebSocket usually, but successful api call allows local optimism if needed
    } catch (error) {
      console.error(`Error updating booking status to ${status}:`, error);
      setError(`Failed to update booking status. Please try again.`);
    }
  };

  // Start Service Modal Logic
  const [showStartModal, setShowStartModal] = useState(false);
  const [selectedBooking, setSelectedBooking] = useState(null);

  const handleStartServiceClick = async (booking) => {
    try {
      // Logic: If status is 'accepted', assume user wants to start 'on_the_way'.
      // If already 'on_the_way' or 'in-progress', just open the modal.
      if (booking.status === 'accepted') {
        await handleStatusUpdate(booking._id, 'on_the_way');
        setSelectedBooking({ ...booking, status: 'on_the_way' });
      } else {
        setSelectedBooking(booking);
      }
      setShowStartModal(true);
    } catch (error) {
      console.error('Error starting service:', error);
    }
  };

  const handleReachedLocation = async () => {
    if (selectedBooking) {
      await handleStatusUpdate(selectedBooking._id, 'in-progress');
      // Update selected booking locally to reflect status change immediately in modal
      setSelectedBooking(prev => ({ ...prev, status: 'in-progress' }));
    }
  };

  const handleMarkCompleted = async () => {
    if (selectedBooking) {
      await handleStatusUpdate(selectedBooking._id, 'completed');
      setShowStartModal(false);
      setSelectedBooking(null);
    }
  }

  const openNavigation = () => {
    if (selectedBooking) {
      const query = selectedBooking.customer?.coordinates
        ? `${selectedBooking.customer.coordinates.lat},${selectedBooking.customer.coordinates.lng}`
        : encodeURIComponent(selectedBooking.address || '');

      window.open(`https://www.google.com/maps/dir/?api=1&destination=${query}`, '_blank');
    }
  };

  const statsCards = [
    {
      name: 'Total Bookings',
      value: stats.totalBookings,
      icon: UserGroupIcon,
      change: '+2.5%',
      changeType: 'positive',
    },
    {
      name: 'Pending',
      value: stats.pendingBookings,
      icon: ClockIcon,
      change: '+3.2%',
      changeType: 'positive',
    },
    {
      name: 'Completed',
      value: stats.completedBookings,
      icon: CheckCircleIcon,
      change: '+1.1%',
      changeType: 'positive',
    },
    {
      name: 'Today\'s Earnings',
      value: `₹${stats.todayEarnings.toLocaleString()}`,
      icon: CurrencyDollarIcon,
      change: '+4.6%',
      changeType: 'positive',
    },
  ];

  // Helper to safely get customer coordinates or default
  const getCustomerLocation = (booking) => {
    if (booking?.customer?.coordinates?.lat && booking?.customer?.coordinates?.lng) {
      return [booking.customer.coordinates.lat, booking.customer.coordinates.lng];
    }
    // Fallback if no coords from customer but we have to show something (Mock or Center on Partner)
    // Here we return null and handle it in render
    return null;
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="rounded-md bg-red-50 p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <ExclamationCircleIcon className="h-5 w-5 text-red-400" aria-hidden="true" />
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">{error}</h3>
            <div className="mt-2 text-sm text-red-700">
              <p>Please refresh the page or try again later.</p>
            </div>
            <div className="mt-4">
              <button
                type="button"
                className="rounded-md bg-red-50 text-sm font-medium text-red-800 hover:bg-red-100 focus:outline-none focus:ring-2 focus:ring-red-600 focus:ring-offset-2 focus:ring-offset-red-50"
                onClick={() => window.location.reload()}
              >
                Refresh
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 space-y-6">
        {/* Stats */}
        <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
          {statsCards.map((stat) => (
            <div
              key={stat.name}
              className="bg-white overflow-hidden shadow rounded-lg"
            >
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <stat.icon className="h-6 w-6 text-gray-400" aria-hidden="true" />
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">
                        {stat.name}
                      </dt>
                      <dd className="flex items-baseline">
                        <div className="text-2xl font-semibold text-gray-900">
                          {stat.value}
                        </div>
                        {stat.change && (
                          <div
                            className={classNames(
                              stat.changeType === 'increase' ? 'text-green-600' : 'text-red-600',
                              'ml-2 flex items-baseline text-sm font-semibold'
                            )}
                          >
                            {stat.change}
                          </div>
                        )}
                      </dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Bookings */}
        <div className="bg-white shadow rounded-lg">
          <Tab.Group>
            <div className="border-b border-gray-200">
              <Tab.List as="nav" className="-mb-px flex space-x-8 px-6">
                <Tab
                  className={({ selected }) =>
                    classNames(
                      selected
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300',
                      'whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm outline-none'
                    )
                  }
                >
                  Available Bookings
                  {availableBookings.length > 0 && (
                    <span className="ml-2 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                      {availableBookings.length}
                    </span>
                  )}
                </Tab>
                <Tab
                  className={({ selected }) =>
                    classNames(
                      selected
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300',
                      'whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm outline-none'
                    )
                  }
                >
                  My Bookings
                  {myBookings.length > 0 && (
                    <span className="ml-2 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                      {myBookings.length}
                    </span>
                  )}
                </Tab>
              </Tab.List>
            </div>
            <Tab.Panels className="p-6">
              <Tab.Panel>
                <BookingList
                  bookings={availableBookings}
                  emptyMessage="No available bookings at the moment"
                  onAccept={handleAcceptBooking}
                  onReject={handleRejectBooking}
                  onStatusUpdate={handleStatusUpdate}
                  onStartServiceClick={handleStartServiceClick}
                />
              </Tab.Panel>
              <Tab.Panel>
                <BookingList
                  bookings={myBookings}
                  emptyMessage="You don't have any active bookings"
                  onStatusUpdate={handleStatusUpdate}
                  onStartServiceClick={handleStartServiceClick}
                />
              </Tab.Panel>
            </Tab.Panels>
          </Tab.Group>
        </div>
      </div>

      {/* Start Service Modal (Bottom Sheet) */}
      {showStartModal && selectedBooking && (
        <div className="fixed inset-0 z-50 flex items-end justify-center bg-black bg-opacity-50 transition-opacity">
          <div className="bg-white w-full max-w-lg mx-auto rounded-t-2xl shadow-xl transform transition-transform duration-300 ease-in-out h-[85vh] flex flex-col overflow-hidden relative">
            {/* Modal Header */}
            <div className="px-4 py-3 border-b flex justify-between items-center bg-gray-50 z-10">
              <h3 className="text-lg font-semibold text-gray-900">Live Tracking</h3>
              <button
                onClick={() => setShowStartModal(false)}
                className="text-gray-500 hover:text-gray-700 p-2"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Map Area */}
            <div className="flex-grow relative bg-gray-100 z-0">
              {(!partnerLocation && !getCustomerLocation(selectedBooking)) ? (
                <div className="flex items-center justify-center h-full text-gray-500">
                  Getting location...
                </div>
              ) : (
                <MapContainer
                  center={partnerLocation ? [partnerLocation.lat, partnerLocation.lng] : (getCustomerLocation(selectedBooking) || [13.0827, 80.2707])}
                  zoom={13}
                  style={{ height: '100%', width: '100%' }}
                >
                  <ChangeView center={partnerLocation ? [partnerLocation.lat, partnerLocation.lng] : (getCustomerLocation(selectedBooking) || [13.0827, 80.2707])} zoom={13} />
                  <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  />
                  {/* Partner Location */}
                  {partnerLocation && (
                    <Marker position={[partnerLocation.lat, partnerLocation.lng]}>
                      <Popup>You are Here</Popup>
                    </Marker>
                  )}
                  {/* Customer Location */}
                  {getCustomerLocation(selectedBooking) && (
                    <Marker position={getCustomerLocation(selectedBooking)}>
                      <Popup>Customer Location</Popup>
                    </Marker>
                  )}
                  {/* Route Line */}
                  {partnerLocation && getCustomerLocation(selectedBooking) && (
                    <Polyline
                      positions={[
                        [partnerLocation.lat, partnerLocation.lng],
                        getCustomerLocation(selectedBooking)
                      ]}
                      color="blue"
                    />
                  )}
                </MapContainer>
              )}
            </div>

            {/* Customer Details & Actions */}
            <div className="p-5 bg-white border-t space-y-4 z-10">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-500">Destination</p>
                  <p className="font-semibold text-gray-900 line-clamp-1">{selectedBooking.address || 'Address not provided'}</p>
                </div>
                <div className="bg-green-100 text-green-700 px-3 py-1 rounded-full text-xs font-bold animate-pulse">
                  {selectedBooking.status === 'on_the_way' ? 'ON THE WAY' : selectedBooking.status === 'in-progress' ? 'IN PROGRESS' : selectedBooking.status}
                </div>
              </div>

              <div className="grid grid-cols-1 gap-3 pt-2">
                {/* 1. Navigate Button */}
                <button
                  onClick={openNavigation}
                  className="w-full flex items-center justify-center px-4 py-3 border border-gray-300 shadow-sm text-base font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                >
                  <svg className="mr-2 h-5 w-5 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 20l-5.447-2.724A1 1 0 013 16.382V5.618a1 1 0 011.447-.894L9 7m0 13l6-3m-6 3V7m6 10l4.553 2.276A1 1 0 0021 18.382V7.618a1 1 0 00-.553-.894L15 4m0 13V4m0 0L9 7" />
                  </svg>
                  Navigate
                </button>

                {/* 2. Reached Location Button - Shown when status is 'on_the_way' */}
                {selectedBooking.status === 'on_the_way' && (
                  <button
                    onClick={handleReachedLocation}
                    className="w-full flex items-center justify-center px-4 py-3 border border-transparent text-base font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 shadow-lg"
                  >
                    Reached Location
                  </button>
                )}

                {/* 3. Mark Service As Completed Button - Shown when status is 'in-progress' (or after being reached) */}
                {selectedBooking.status === 'in-progress' && (
                  <button
                    onClick={handleMarkCompleted}
                    className="w-full flex items-center justify-center px-4 py-3 border border-transparent text-base font-medium rounded-md text-white bg-green-600 hover:bg-green-700 shadow-lg"
                  >
                    Mark Service As Completed
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
