import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  ClipboardDocumentListIcon,
  CurrencyDollarIcon,
  CheckCircleIcon,
  BellIcon,
  CalendarIcon,
  MapPinIcon,
  PhoneIcon,
  CheckIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalBookings: 45,
    completedBookings: 38,
    pendingBookings: 3,
    todayEarnings: 2500,
    totalEarnings: 45000,
    averageRating: 4.8
  });

  const [recentBookings, setRecentBookings] = useState([
    {
      _id: '1',
      customerName: 'John Doe',
      customerPhone: '+91 98765 43210',
      service: { name: 'Home Cleaning', category: 'Cleaning' },
      status: 'pending',
      scheduledDate: new Date(),
      address: '123 Main St, Bangalore',
      pricing: { totalAmount: 1500 },
      duration: '2 hours',
      urgency: 'high'
    },
    {
      _id: '2',
      customerName: 'Jane Smith',
      customerPhone: '+91 87654 32109',
      service: { name: 'Plumbing Service', category: 'Repair' },
      status: 'confirmed',
      scheduledDate: new Date(Date.now() + 2 * 60 * 60 * 1000),
      address: '456 Oak Ave, Bangalore',
      pricing: { totalAmount: 2000 },
      duration: '1.5 hours',
      urgency: 'medium'
    },
    {
      _id: '3',
      customerName: 'Mike Johnson',
      customerPhone: '+91 76543 21098',
      service: { name: 'AC Repair', category: 'Appliance' },
      status: 'in-progress',
      scheduledDate: new Date(),
      address: '789 Pine Rd, Bangalore',
      pricing: { totalAmount: 1800 },
      duration: '3 hours',
      urgency: 'low'
    }
  ]);

  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);

  // Auto new bookings simulation
  useEffect(() => {
    const interval = setInterval(() => {
      if (Math.random() > 0.8) {
        const newBooking = {
          _id: Date.now().toString(),
          customerName: ['Sarah Wilson', 'David Lee', 'Emma Brown'][Math.floor(Math.random() * 3)],
          customerPhone: '+91 9' + Math.floor(Math.random() * 100000000),
          service: { 
            name: ['Electric Repair', 'Deep Cleaning', 'Carpentry'][Math.floor(Math.random() * 3)], 
            category: 'General' 
          },
          status: 'pending',
          scheduledDate: new Date(Date.now() + Math.random() * 24 * 60 * 60 * 1000),
          address: 'New Service Location',
          pricing: { totalAmount: Math.floor(Math.random() * 3000) + 500 },
          duration: (Math.random() * 4 + 1).toFixed(1) + ' hours',
          urgency: ['high', 'medium', 'low'][Math.floor(Math.random() * 3)]
        };

        setRecentBookings(prev => [newBooking, ...prev.slice(0, 4)]);
        setNotifications(prev => [
          { type: 'new_booking', message: `New booking request from ${newBooking.customerName}!`, timestamp: new Date() },
          ...prev.slice(0, 2)
        ]);
      }
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  // Accept booking
  const handleAcceptBooking = (bookingId) => {
    setRecentBookings(prev => prev.map(b => b._id === bookingId ? { ...b, status: 'confirmed' } : b));
    setStats(prev => ({ ...prev, pendingBookings: Math.max(0, prev.pendingBookings - 1) }));

    setNotifications(prev => [{ type: 'success', message: 'Booking accepted successfully!', timestamp: new Date() }, ...prev]);

    setTimeout(() => {
      setRecentBookings(prev => prev.map(b => b._id === bookingId ? { ...b, status: 'in-progress' } : b));
    }, 5000);

    setTimeout(() => {
      setRecentBookings(prev => prev.map(b => b._id === bookingId ? { ...b, status: 'completed' } : b));
      setStats(prev => ({
        ...prev,
        completedBookings: prev.completedBookings + 1,
        todayEarnings: prev.todayEarnings + 2000
      }));
    }, 10000);
  };

  // Reject booking
  const handleRejectBooking = (bookingId) => {
    setRecentBookings(prev => prev.filter(b => b._id !== bookingId));
    setStats(prev => ({ ...prev, pendingBookings: Math.max(0, prev.pendingBookings - 1) }));

    setNotifications(prev => [{ type: 'warning', message: 'Booking rejected.', timestamp: new Date() }, ...prev]);
  };

  // Color helpers
  const getUrgencyBadge = (level) => {
    const colors = {
      high: { bg: '#fee2e2', text: '#dc2626' },
      medium: { bg: '#fef3c7', text: '#d97706' },
      low: { bg: '#dcfce7', text: '#16a34a' }
    };
    return colors[level] || colors.low;
  };

  const statusColor = {
    pending: { bg: '#fef3c7', text: '#92400e' },
    confirmed: { bg: '#dbeafe', text: '#1e40af' },
    'in-progress': { bg: '#ede9fe', text: '#5b21b6' },
    completed: { bg: '#d1fae5', text: '#065f46' }
  };

  // Loading mimic
  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 800);
    return () => clearTimeout(timer);
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6 bg-gray-100">
      
      {/* Header */}
      <div className="bg-white shadow rounded-lg p-6">
        <h1 className="text-3xl font-bold">Welcome back, Partner!</h1>
        <p className="text-gray-600 mt-2">Here's what's happening today</p>
      </div>

      {/* ---- Stats Grid ---- */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">

        {/* Total Bookings */}
        <div className="bg-white shadow rounded-lg p-6 flex items-center">
          <div className="p-3 bg-blue-100 rounded-full">
            <ClipboardDocumentListIcon className="h-6 w-6 text-blue-600" />
          </div>
          <div className="ml-4">
            <p className="text-sm text-gray-500">Total Bookings</p>
            <p className="text-2xl font-bold">{stats.totalBookings}</p>
          </div>
        </div>

        {/* Completed */}
        <div className="bg-white shadow rounded-lg p-6 flex items-center">
          <div className="p-3 bg-green-100 rounded-full">
            <CheckCircleIcon className="h-6 w-6 text-green-600" />
          </div>
          <div className="ml-4">
            <p className="text-sm text-gray-500">Completed</p>
            <p className="text-2xl font-bold">{stats.completedBookings}</p>
          </div>
        </div>

        {/* Today Earnings */}
        <div className="bg-white shadow rounded-lg p-6 flex items-center">
          <div className="p-3 bg-purple-100 rounded-full">
            <CurrencyDollarIcon className="h-6 w-6 text-purple-600" />
          </div>
          <div className="ml-4">
            <p className="text-sm text-gray-500">Today's Earnings</p>
            <p className="text-2xl font-bold">₹{stats.todayEarnings.toLocaleString()}</p>
          </div>
        </div>
      </div>

      {/* Recent Bookings */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold">Recent Bookings</h2>
          <Link to="/partner/bookings" className="text-blue-600 hover:underline">View All</Link>
        </div>
        
        <div className="space-y-4">
          {recentBookings.map(booking => (
            <div key={booking._id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="font-medium">{booking.service.name}</h3>
                  <p className="text-sm text-gray-600">{booking.customerName}</p>
                  <div className="flex items-center mt-1 text-sm text-gray-500">
                    <CalendarIcon className="h-4 w-4 mr-1" />
                    {new Date(booking.scheduledDate).toLocaleString()}
                  </div>
                  <div className="flex items-center mt-1 text-sm text-gray-500">
                    <MapPinIcon className="h-4 w-4 mr-1" />
                    {booking.address}
                  </div>
                </div>
                
                <div className="flex flex-col items-end">
                  <span 
                    className="px-2 py-1 rounded-full text-xs font-medium"
                    style={{
                      backgroundColor: statusColor[booking.status].bg,
                      color: statusColor[booking.status].text
                    }}
                  >
                    {booking.status.replace('-', ' ')}
                  </span>
                  <span className="mt-2 text-lg font-semibold">₹{booking.pricing.totalAmount}</span>
                </div>
              </div>
              
              <div className="mt-3 pt-3 border-t flex justify-between items-center">
                <div className="flex items-center">
                  <span 
                    className="px-2 py-1 rounded-full text-xs"
                    style={{
                      backgroundColor: getUrgencyBadge(booking.urgency).bg,
                      color: getUrgencyBadge(booking.urgency).text
                    }}
                  >
                    {booking.urgency} priority
                  </span>
                  <span className="ml-2 text-sm text-gray-500">{booking.duration}</span>
                </div>
                
                <div className="space-x-2">
                  {booking.status === 'pending' && (
                    <>
                      <button 
                        onClick={() => handleAcceptBooking(booking._id)}
                        className="px-3 py-1 bg-green-100 text-green-700 rounded-md text-sm hover:bg-green-200"
                      >
                        Accept
                      </button>
                      <button 
                        onClick={() => handleRejectBooking(booking._id)}
                        className="px-3 py-1 bg-red-100 text-red-700 rounded-md text-sm hover:bg-red-200"
                      >
                        Reject
                      </button>
                    </>
                  )}
                  {booking.status === 'in-progress' && (
                    <button 
                      className="px-3 py-1 bg-blue-100 text-blue-700 rounded-md text-sm hover:bg-blue-200"
                    >
                      Start Service
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      {/* Notifications */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Notifications</h2>
        <div className="space-y-3">
          {notifications.length > 0 ? (
            notifications.map((notif, index) => (
              <div 
                key={index} 
                className={`p-3 rounded-lg ${notif.type === 'new_booking' ? 'bg-blue-50' : notif.type === 'success' ? 'bg-green-50' : 'bg-yellow-50'}`}
              >
                <div className="flex justify-between">
                  <p className="text-sm">{notif.message}</p>
                  <span className="text-xs text-gray-500">
                    {new Date(notif.timestamp).toLocaleTimeString()}
                  </span>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-500 py-4">No new notifications</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
