import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  ClipboardDocumentListIcon,
  CurrencyDollarIcon,
  ClockIcon,
  CheckCircleIcon,
  StarIcon,
  CalendarIcon,
  UserGroupIcon,
  TrendingUpIcon,
  BellIcon,
  MapPinIcon,
  PhoneIcon,
  XMarkIcon,
  CheckIcon
} from '@heroicons/react/24/outline';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalBookings: 45,
    completedBookings: 38,
    pendingBookings: 3,
    todayEarnings: 2500,
    totalEarnings: 45000,
    averageRating: 4.8
  });

  const [recentBookings, setRecentBookings] = useState([
    {
      _id: '1',
      customerName: 'John Doe',
      customerPhone: '+91 98765 43210',
      service: { name: 'Home Cleaning', category: 'Cleaning' },
      status: 'pending',
      scheduledDate: new Date(),
      address: '123 Main St, Bangalore',
      pricing: { totalAmount: 1500 },
      duration: '2 hours',
      urgency: 'high'
    },
    {
      _id: '2',
      customerName: 'Jane Smith',
      customerPhone: '+91 87654 32109',
      service: { name: 'Plumbing Service', category: 'Repair' },
      status: 'confirmed',
      scheduledDate: new Date(Date.now() + 2 * 60 * 60 * 1000),
      address: '456 Oak Ave, Bangalore',
      pricing: { totalAmount: 2000 },
      duration: '1.5 hours',
      urgency: 'medium'
    },
    {
      _id: '3',
      customerName: 'Mike Johnson',
      customerPhone: '+91 76543 21098',
      service: { name: 'AC Repair', category: 'Appliance' },
      status: 'in-progress',
      scheduledDate: new Date(),
      address: '789 Pine Rd, Bangalore',
      pricing: { totalAmount: 1800 },
      duration: '3 hours',
      urgency: 'low'
    }
  ]);

  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const interval = setInterval(() => {
      const random = Math.random();
      if (random > 0.8) {
        const newBooking = {
          _id: Date.now().toString(),
          customerName: ['Sarah Wilson', 'David Lee', 'Emma Brown'][Math.floor(Math.random() * 3)],
          customerPhone: '+91 9' + Math.floor(Math.random() * 100000000),
          service: { 
            name: ['Electric Repair', 'Deep Cleaning', 'Carpentry'][Math.floor(Math.random() * 3)], 
            category: 'General' 
          },
          status: 'pending',
          scheduledDate: new Date(Date.now() + Math.random() * 24 * 60 * 60 * 1000),
          address: 'New Service Location',
          pricing: { totalAmount: Math.floor(Math.random() * 3000) + 500 },
          duration: (Math.random() * 4 + 1).toFixed(1) + ' hours',
          urgency: ['high', 'medium', 'low'][Math.floor(Math.random() * 3)]
        };
        
        setRecentBookings(prev => [newBooking, ...prev.slice(0, 4)]);
        setNotifications(prev => [{ type: 'new_booking', message: `New booking request from ${newBooking.customerName}!`, timestamp: new Date() }, ...prev.slice(0, 2)]);
      }
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  const handleAcceptBooking = (bookingId) => {
    setRecentBookings(prev => prev.map(booking => booking._id === bookingId ? { ...booking, status: 'confirmed' } : booking));
    setStats(prev => ({ ...prev, pendingBookings: Math.max(0, prev.pendingBookings - 1) }));

    setNotifications(prev => [{ type: 'success', message: 'Booking accepted successfully!', timestamp: new Date() }, ...prev]);

    setTimeout(() => {
      setRecentBookings(prev => prev.map(booking => booking._id === bookingId ? { ...booking, status: 'in-progress' } : booking));
      setNotifications(prev => [{ type: 'info', message: 'Service in progress...', timestamp: new Date() }, ...prev]);
    }, 5000);

    setTimeout(() => {
      setRecentBookings(prev => prev.map(booking => booking._id === bookingId ? { ...booking, status: 'completed' } : booking));
      setStats(prev => ({ ...prev, completedBookings: prev.completedBookings + 1, todayEarnings: prev.todayEarnings + 2000 }));
      setNotifications(prev => [{ type: 'success', message: 'Service completed successfully! Payment received.', timestamp: new Date() }, ...prev]);
    }, 10000);
  };

  const handleRejectBooking = (bookingId) => {
    setRecentBookings(prev => prev.filter(booking => booking._id !== bookingId));
    setStats(prev => ({ ...prev, pendingBookings: Math.max(0, prev.pendingBookings - 1) }));

    setNotifications(prev => [{ type: 'warning', message: 'Booking rejected.', timestamp: new Date() }, ...prev]);
  };

  const getUrgencyColor = (urgency) => {
    switch (urgency) {
      case 'high':
        return 'red';
      case 'medium':
        return 'yellow';
      case 'low':
        return 'green';
      default:
        return 'gray';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending':
        return '#fef3c7';
      case 'confirmed':
        return '#dbeafe';
      case 'in-progress':
        return '#ede9fe';
      case 'completed':
        return '#d1fae5';
      default:
        return '#f3f4f6';
    }
  };

  const getStatusTextColor = (status) => {
    switch (status) {
      case 'pending':
        return '#92400e';
      case 'confirmed':
        return '#1e40af';
      case 'in-progress':
        return '#5b21b6';
      case 'completed':
        return '#065f46';
      default:
        return '#374151';
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6" style={{ padding: '24px', backgroundColor: '#f9fafb' }}>
      {/* Header */}
      <div className="bg-white shadow rounded-lg p-6" style={{ backgroundColor: 'white', boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', borderRadius: '8px', padding: '24px' }}>
        <h1 className="text-3xl font-bold text-gray-900" style={{ fontSize: '30px', fontWeight: 'bold', color: '#111827' }}>Welcome back, Partner!</h1>
        <p className="text-gray-600 mt-2" style={{ color: '#6b7280', marginTop: '8px' }}>Here's what's happening with your business today</p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '24px' }}>
        <div className="bg-white rounded-lg shadow p-6" style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', padding: '24px' }}>
          <div className="flex items-center" style={{ display: 'flex', alignItems: 'center' }}>
            <div className="p-3 bg-blue-100 rounded-full" style={{ padding: '12px', backgroundColor: '#dbeafe', borderRadius: '50%' }}>
              <ClipboardDocumentListIcon className="h-6 w-6 text-blue-600" style={{ height: '24px', width: '24px', color: '#2563eb' }} />
            </div>
            <div className="ml-4" style={{ marginLeft: '16px' }}>
              <p className="text-sm text-gray-500" style={{ fontSize: '14px', color: '#6b7280' }}>Total Bookings</p>
              <p className="text-2xl font-semibold text-gray-900" style={{ fontSize: '24px', fontWeight: '600', color: '#111827' }}>{stats.totalBookings}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6" style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', padding: '24px' }}>
          <div className="flex items-center" style={{ display: 'flex', alignItems: 'center' }}>
            <div className="p-3 bg-green-100 rounded-full" style={{ padding: '12px', backgroundColor: '#d1fae5', borderRadius: '50%' }}>
              <CheckCircleIcon className="h-6 w-6 text-green-600" style={{ height: '24px', width: '24px', color: '#059669' }} />
            </div>
            <div className="ml-4" style={{ marginLeft: '16px' }}>
              <p className="text-sm text-gray-500" style={{ fontSize: '14px', color: '#6b7280' }}>Completed</p>
              <p className="text-2xl font-semibold text-gray-900" style={{ fontSize: '24px', fontWeight: '600', color: '#111827' }}>{stats.completedBookings}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6" style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)', padding: '24px' }}>
          <div className="flex items-center" style={{ display: 'flex', alignItems: 'center' }}>
            <div className="p-3 bg-purple-100 rounded-full" style={{ padding: '12px', backgroundColor: '#ede9fe', borderRadius: '50%' }}>
              <CurrencyDollarIcon className="h-6 w-6 text-purple-600" style={{ height: '24px', width: '24px', color: '#7c3aed' }} />
            </div>
            <div className="ml-4" style={{ marginLeft: '16px' }}>
              <p className="text-sm text-gray-500" style={{ fontSize: '14px', color: '#6b7280' }}>Today's Earnings</p>
              <p className="text-2xl font-semibold text-gray-900" style={{ fontSize: '24px', fontWeight: '600', color: '#111827' }}>₹{stats.todayEarnings}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Recent Bookings */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-lg font-medium text-gray-900">Recent Bookings</h2>
          <Link to="/bookings" className="text-sm font-medium text-blue-600 hover:text-blue-500">
            View all
          </Link>
        </div>
        
        <div className="space-y-4">
          {recentBookings.map((booking) => (
            <div key={booking._id} className="border-b border-gray-200 pb-4 last:border-0 last:pb-0">
              <div className="flex items-center">
                <div className="flex-1">
                  <p className="text-sm font-medium text-gray-900">{booking.customerName}</p>
                  <p className="text-sm text-gray-500">{booking.service?.name}</p>
                </div>
                <div className="ml-4">
                  <span className={`px-2 py-1 text-xs rounded-full ${
                    booking.status === 'completed' 
                      ? 'bg-green-100 text-green-800' 
                      : booking.status === 'pending'
                      ? 'bg-yellow-100 text-yellow-800'
                      : 'bg-blue-100 text-blue-800'
                  }`}>
                    {booking.status}
                  </span>
                </div>
              </div>
              <div className="mt-2 flex justify-between items-center">
                <div className="text-sm text-gray-500">
                  {booking.scheduledDate && new Date(booking.scheduledDate).toLocaleDateString()} • {booking.duration}
                </div>
                <div className="text-sm font-medium">
                  ₹{booking.pricing?.totalAmount || '0'}
                </div>
              </div>
              <div className="mt-1 flex items-center text-xs text-gray-500">
                <MapPinIcon className="h-3 w-3 mr-1" />
                {booking.address}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
