import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import partnerService from '../../services/partnerService';
import Navbar from '../common/Navbar';

const ServiceInProgress = () => {
    const { bookingId } = useParams();
    const navigate = useNavigate();
    const [booking, setBooking] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Fetch booking details on mount and correct status if needed
    useEffect(() => {
        const fetchAndCorrectBooking = async () => {
            try {
                const response = await partnerService.getBookingById(bookingId);
                const fetchedBooking = response.data;
                setBooking(fetchedBooking);

                // If status is 'on_the_way', we must update to 'in_progress' because we are on the active service page
                if (fetchedBooking.status === 'on_the_way') {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    setBooking(prev => ({ ...prev, status: 'in_progress' }));
                }
            } catch (err) {
                console.error('Error fetching/correcting booking:', err);
                // Don't show error immediately, allow user to try "Mark Completed" which might work or fail with more info
            }
        };
        fetchAndCorrectBooking();
    }, [bookingId]);

    const handleCompleteService = async () => {
        try {
            setLoading(true);
            await partnerService.updateBookingStatus(bookingId, 'completed');
            setLoading(false);
            navigate('/dashboard'); // Go back to dashboard after completion
        } catch (err) {
            console.error('Error completing service:', err);
            // Fallback: If error is restricted transition, try force updating to in_progress first
            if (err.response && err.response.status === 400 && booking?.status !== 'in_progress') {
                try {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    await partnerService.updateBookingStatus(bookingId, 'completed');
                    setLoading(false);
                    navigate('/dashboard');
                    return;
                } catch (retryErr) {
                    console.error('Retry failed:', retryErr);
                }
            }

            setError('Failed to list service as completed. Please try again.');
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col">
            <Navbar />

            <div className="flex-grow flex flex-col items-center justify-center p-6 space-y-8">

                {/* Bike & Cogs Animation */}
                <div className="relative w-full h-48 flex items-center justify-center overflow-hidden bg-white mb-4">
                    {/* Speed Lines Background */}
                    <div className="absolute inset-0 opacity-20">
                        <div className="w-full h-full bg-[linear-gradient(90deg,transparent_50%,#cbd5e1_50%)] bg-[length:40px_100%] animate-speed-lines"></div>
                    </div>

                    <div className="relative z-10 flex flex-col items-center">
                        <div className="relative animate-shake">
                            {/* Bike Frame (Simplified) */}
                            <svg className="w-32 h-24 text-blue-600 drop-shadow-lg" viewBox="0 0 100 60" fill="none" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round">
                                <path d="M20 50 L40 50 L55 20 L25 20 L20 50" /> {/* Rear Triangle */}
                                <path d="M55 20 L80 20 L70 50" /> {/* Front Fork */}
                                <path d="M25 20 L45 5" /> {/* Seat Post */}
                                <path d="M40 5 L50 5" /> {/* Seat */}
                                <path d="M75 10 L80 20" /> {/* Handlebar Stem */}
                                <path d="M70 10 L85 10" /> {/* Handlebar */}
                            </svg>

                            {/* Wheels (Cogs) */}
                            {/* Rear Wheel */}
                            <div className="absolute left-[8px] top-[40px] w-[24px] h-[24px]">
                                <svg className="w-full h-full text-gray-700 animate-spin" style={{ animationDuration: '0.5s' }} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                    <circle cx="12" cy="12" r="10" />
                                    <path d="M12 2 L12 22 M2 12 L22 12 M4.9 4.9 L19.1 19.1 M4.9 19.1 L19.1 4.9" />
                                </svg>
                            </div>

                            {/* Front Wheel */}
                            <div className="absolute left-[58px] top-[40px] w-[24px] h-[24px]">
                                <svg className="w-full h-full text-gray-700 animate-spin" style={{ animationDuration: '0.5s' }} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                    <circle cx="12" cy="12" r="10" />
                                    <path d="M12 2 L12 22 M2 12 L22 12 M4.9 4.9 L19.1 19.1 M4.9 19.1 L19.1 4.9" />
                                </svg>
                            </div>

                            {/* Rotating Cog (Service mechanism) - Floating above or near */}
                            <div className="absolute -top-6 -right-6 text-yellow-500">
                                <svg className="w-10 h-10 animate-spin" style={{ animationDuration: '3s' }} viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M12 15a3 3 0 100-6 3 3 0 000 6z" />
                                    <path fillRule="evenodd" d="M1.323 11.447C2.811 6.976 7.028 3.75 12.001 3.75c4.97 0 9.185 3.223 10.675 7.69.12.362.12.752 0 1.113-1.487 4.471-5.705 7.697-10.677 7.697-4.97 0-9.186-3.223-10.675-7.69a1.762 1.762 0 010-1.113zM17.25 12a5.25 5.25 0 11-10.5 0 5.25 5.25 0 0110.5 0z" clipRule="evenodd" className="hidden" />
                                    {/* Actual Cog Path */}
                                    <path d="M11.99 2C6.47 2 2 6.48 2 12s4.47 10 9.99 10C17.52 22 22 17.52 22 12S17.52 2 11.99 2zm6.93 6h-2.95a6.002 6.002 0 00-1.92-2.93l2.1-2.1a8.046 8.046 0 012.77 5.03zM12 4.02c1.37 0 2.65.36 3.78 1.01l-2.1 2.1a4.02 4.02 0 00-3.35 0l-2.1-2.1A7.993 7.993 0 0112 4.02zM6.07 7.06l2.1 2.1a5.98 5.98 0 00-1.91 2.93H3.3a8.026 8.026 0 012.77-5.03zM3.3 14h2.95a5.98 5.98 0 001.91 2.93l-2.1 2.1a8.03 8.03 0 01-2.76-5.03zM12 19.98a7.993 7.993 0 01-3.78-1.01l2.1-2.1a4.02 4.02 0 003.35 0l2.1 2.1a7.993 7.993 0 01-3.78 1.01zM17.93 16.94l-2.1-2.1a6.002 6.002 0 001.92-2.93h2.95a8.046 8.046 0 01-2.77 5.03z" />
                                </svg>
                            </div>

                            {/* Another smaller cog */}
                            <div className="absolute top-8 left-10 text-gray-400 opacity-60">
                                <svg className="w-6 h-6 animate-spin" style={{ animationDuration: '2s', animationDirection: 'reverse' }} viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M12 15a3 3 0 100-6 3 3 0 000 6z" />
                                    <path d="M19.4 15a1.65 1.65 0 00.33 1.82l.06.06a2 2 0 010 2.83 2 2 0 01-2.83 0l-.06-.06a1.65 1.65 0 00-1.82-.33 1.65 1.65 0 00-1 1.51V21a2 2 0 01-2 2 2 2 0 01-2-2v-.09A1.65 1.65 0 009 19.4a1.65 1.65 0 00-1.82.33l-.06.06a2 2 0 01-2.83 0 2 2 0 010-2.83l.06-.06a1.65 1.65 0 00.33-1.82 1.65 1.65 0 00-1.51-1H3a2 2 0 01-2-2 2 2 0 012-2h.09A1.65 1.65 0 004.6 9a1.65 1.65 0 00-.33-1.82l-.06-.06a2 2 0 010-2.83 2 2 0 012.83 0l.06.06a1.65 1.65 0 001.82.33H9a1.65 1.65 0 001-1.51V3a2 2 0 012-2 2 2 0 012 2v.09a1.65 1.65 0 001 1.51 1.65 1.65 0 001.82-.33l.06-.06a2 2 0 012.83 0 2 2 0 010 2.83l-.06.06a1.65 1.65 0 00-.33 1.82V9a1.65 1.65 0 001.51 1H21a2 2 0 012 2 2 2 0 01-2 2h-.09a1.65 1.65 0 00-1.51 1z" />
                                </svg>
                            </div>

                        </div>
                    </div>

                    {/* Motion Blur / Speed Lines on Bike */}
                    <div className="absolute top-1/2 left-1/4 w-32 h-1 bg-gradient-to-r from-transparent to-gray-200 animate-speed-lines" style={{ animationDelay: '0.1s' }}></div>
                    <div className="absolute top-1/3 right-1/4 w-24 h-1 bg-gradient-to-r from-transparent to-gray-200 animate-speed-lines" style={{ animationDelay: '0.3s' }}></div>
                </div>

                <div className="text-center space-y-2">
                    <h2 className="text-2xl font-bold text-gray-900">Service in Progress</h2>
                    <p className="text-gray-500">Please complete the service requested by the customer.</p>
                </div>

                {/* Animated Loading Bar */}
                <div className="w-full max-w-md space-y-2">
                    <div className="flex justify-between text-xs text-gray-400 font-semibold uppercase tracking-wider">
                        <span>Started</span>
                        <span>In Progress</span>
                    </div>
                    <div className="h-4 bg-gray-200 rounded-full overflow-hidden relative">
                        <div className="absolute top-0 left-0 bottom-0 bg-blue-600 w-full animate-progress-stripes bg-[length:40px_40px] bg-gradient-to-r from-blue-500 via-blue-600 to-blue-500"></div>
                        {/* CSS Animation for moving stripes would be ideal, simpler solid bar for now or keyframes in index.css */}
                        <div className="absolute top-0 left-0 h-full bg-blue-500 animate-[pulse_2s_cubic-bezier(0.4,0,0.6,1)_infinite] w-full origin-left"></div>
                    </div>
                    <p className="text-center text-sm text-blue-600 font-medium animate-pulse">Running...</p>
                </div>

                {error && (
                    <div className="p-4 bg-red-50 text-red-700 rounded-md">
                        {error}
                    </div>
                )}

                <div className="w-full max-w-md pt-8">
                    <button
                        onClick={handleCompleteService}
                        disabled={loading}
                        className="w-full flex items-center justify-center px-6 py-4 border border-transparent text-lg font-medium rounded-lg text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 shadow-xl transition-transform transform active:scale-95 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {loading ? (
                            <>
                                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                </svg>
                                Completing...
                            </>
                        ) : (
                            'Mark Service As Completed'
                        )}
                    </button>
                </div>

            </div>
        </div>
    );
};

export default ServiceInProgress;
