import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import partnerService from '../../services/partnerService';
import paymentServiceUI from '../../services/paymentService';
import Navbar from '../common/Navbar';

const ServiceInProgress = () => {
    const { bookingId } = useParams();
    const navigate = useNavigate();
    const [booking, setBooking] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [selectedPayment, setSelectedPayment] = useState(null);
    const [showPaymentOptions, setShowPaymentOptions] = useState(false);

    const paymentMethods = [
        { id: 'cod', name: 'Cash on Delivery', icon: 'M15 8.25V7.5a2.25 2.25 0 00-2.25-2.25h-1.5a2.25 2.25 0 00-2.25 2.25v.75m-3 0V15a2.25 2.25 0 002.25 2.25h9a2.25 2.25 0 002.25-2.25V8.25m-12 0h12M12 11.25v.75m0 2.25v.75', color: '#22c55e' },
        { id: 'gpay', name: 'GPay (Google Pay)', icon: 'M10.5 1.5H8.25A2.25 2.25 0 006 3.75v16.5a2.25 2.25 0 002.25 2.25h7.5A2.25 2.25 0 0018 20.25V3.75a2.25 2.25 0 00-2.25-2.25H13.5m-3 0V3h3V1.5m-3 0h3m-3 18.75h3', color: '#4285F4' },
        { id: 'phonepay', name: 'PhonePe', icon: 'M10.5 1.5H8.25A2.25 2.25 0 006 3.75v16.5a2.25 2.25 0 002.25 2.25h7.5A2.25 2.25 0 0018 20.25V3.75a2.25 2.25 0 00-2.25-2.25H13.5m-3 0V3h3V1.5m-3 0h3m-3 18.75h3', color: '#6739b7' },
        { id: 'paytm', name: 'Paytm', icon: 'M21 12a2.25 2.25 0 00-2.25-2.25H15a3 3 0 11-6 0H5.25A2.25 2.25 0 003 12m18 0v6a2.25 2.25 0 01-2.25 2.25H5.25A2.25 2.25 0 013 18v-6m18 0V9M3 12V9m18 0a2.25 2.25 0 00-2.25-2.25H5.25A2.25 2.25 0 003 9m18 0V6a2.25 2.25 0 00-2.25-2.25H5.25A2.25 2.25 0 003 6v3', color: '#00baf2' },
        { id: 'amazonpay', name: 'Amazon Pay', icon: 'M2.25 8.25h19.5M2.25 9h19.5m-16.5 5.25h6m-6 2.25h3m-3.75 3h15a2.25 2.25 0 002.25-2.25V6.75A2.25 2.25 0 0019.5 4.5h-15a2.25 2.25 0 00-2.25 2.25v10.5A2.25 2.25 0 004.5 19.5z', color: '#ff9900' },
        { id: 'card', name: 'Credit/Debit Card', icon: 'M2.25 8.25h19.5M2.25 9h19.5m-16.5 5.25h6m-6 2.25h3m-3.75 3h15a2.25 2.25 0 002.25-2.25V6.75A2.25 2.25 0 0019.5 4.5h-15a2.25 2.25 0 00-2.25 2.25v10.5A2.25 2.25 0 004.5 19.5z', color: '#444' }
    ];

    useEffect(() => {
        const fetchAndCorrectBooking = async () => {
            try {
                const response = await partnerService.getBookingById(bookingId);
                const fetchedBooking = response.data;
                setBooking(fetchedBooking);

                if (fetchedBooking.status === 'on_the_way') {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    setBooking(prev => ({ ...prev, status: 'in_progress' }));
                }
            } catch (err) {
                console.error('Error fetching/correcting booking:', err);
            }
        };
        fetchAndCorrectBooking();
    }, [bookingId]);

    const handleCompleteService = async () => {
        if (!selectedPayment) return;

        try {
            setLoading(true);

            if (selectedPayment === 'cod') {
                await partnerService.updateBookingStatus(bookingId, 'completed', {
                    notes: `Payment received via CASH (recorded by partner)`
                });
                navigate('/dashboard');
            } else {
                await paymentServiceUI.processPayment({
                    amount: booking?.pricing?.totalAmount || 0,
                    order_id: booking?._id,
                    customerName: booking?.customer?.name || 'Customer',
                    email: booking?.customer?.email || 'customer@citybell.com',
                    contact: booking?.customer?.phone || '9876543210',
                    description: `Service completion for ${booking?.title}`
                }, {
                    onSuccess: async () => {
                        navigate('/dashboard');
                    },
                    onError: (err) => {
                        setError(err.message || 'Payment failed');
                        setLoading(false);
                    },
                    onCancel: () => {
                        setLoading(false);
                    }
                });
            }
        } catch (err) {
            console.error('Error completing service:', err);
            setError('Failed to complete service. Please try again.');
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col antialiased">
            <Navbar />

            <div className="flex-grow flex flex-col items-center p-6 space-y-8 max-w-2xl mx-auto w-full">

                {/* Animations Container (Restored to CSS/SVG-based Premium Design) */}
                <div className="relative w-full h-48 flex items-center justify-center overflow-hidden">
                    {/* Background Cogs */}
                    <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                        <div className="relative animate-cogs-pop opacity-20">
                            {/* Large Gear */}
                            <svg className="w-32 h-32 text-blue-600 animate-spin" style={{ animationDuration: '8s' }} fill="currentColor" viewBox="0 0 24 24">
                                <path d="M12 15a3 3 0 100-6 3 3 0 000 6z" />
                                <path fillRule="evenodd" d="M11.323 3.946c.215-.83 1.14-.83 1.354 0a.726.726 0 001.03.506.694.694 0 01.915.368 1.173 1.173 0 001.443.504.691.691 0 01.756.57c.23.81 1.136.81 1.366 0a.689.689 0 01.756-.57 1.175 1.175 0 001.443-.504.694.694 0 01.915-.368.726.726 0 001.03-.506c.215-.83 1.14-.83 1.354 0a.726.726 0 001.03.506.694.694 0 01.915.368 1.173 1.173 0 001.443.504.691.691 0 01.756.57c.23.81 1.136.81 1.366 0a.689.689 0 01.756-.57 1.175 1.175 0 001.443-.504.694.694 0 01.915-.368.726.726 0 001.03-.506" />
                                <path d="M19.44 11c.22 0 .45-.07.67-.22 1.39-.93 2.1-2.53 1.81-4.13-.28-1.6-1.53-2.85-3.13-3.13-1.6-.28-3.2.42-4.13 1.81-.15.22-.22.45-.22.67V11h5z" />
                                <path d="M12 24c6.627 0 12-5.373 12-12S18.627 0 12 0 0 5.373 0 12s5.373 12 12 12zm0-2c-5.523 0-10-4.477-10-10S6.477 2 12 2s10 4.477 10 10-4.477 10-10 10z" />
                                <path d="M12 19c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm0-2c-2.761 0-5-2.239-5-5s2.239-5 5-5 5 2.239 5 5-2.239 5-5 5z" />
                            </svg>
                            {/* Small Gear */}
                            <svg className="w-16 h-16 text-blue-400 animate-spin absolute -bottom-4 -right-4" style={{ animationDuration: '12s', animationDirection: 'reverse' }} fill="currentColor" viewBox="0 0 24 24">
                                <path d="M12 15a3 3 0 100-6 3 3 0 000 6z" />
                                <path d="M12 19c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm0-2c-2.761 0-5-2.239-5-5s2.239-5 5-5 5 2.239 5 5-2.239 5-5 5z" />
                            </svg>
                        </div>
                    </div>

                    {/* Driving Bike */}
                    <div className="absolute inset-0 flex items-center justify-center animate-drive-pass">
                        <svg className="w-24 h-24 text-blue-600 drop-shadow-2xl" fill="currentColor" viewBox="0 0 24 24">
                            <path d="M21 16c0 1.1-.9 2-2 2h-1.41c-.26-1.14-1.27-2-2.59-2s-2.33.86-2.59 2H10.5c0-1.1-.9-2-2-2S6.5 16.9 6.5 18H5c-1.1 0-2-.9-2-2V7h2V5c0-1.1.9-2 2-2h4c1.1 0 2 .9 2 2v2h2l3.5 3.5c.32.32.5.75.5 1.21V16zM7 5v2h4V5H7zm12 6.5L16.5 9H13v3h6v-.5zM8.5 18c.83 0 1.5.67 1.5 1.5S9.33 21 8.5 21 7 20.33 7 19.5 7.67 18 8.5 18zm8.5 0c.83 0 1.5.67 1.5 1.5s-.67 1.5-1.5 1.5-1.5-.67-1.5-1.5.67-1.5 1.5-1.5z" />
                        </svg>
                    </div>
                </div>

                <div className="text-center space-y-2">
                    <h2 className="text-3xl font-black text-gray-900 tracking-tight">Service in Progress</h2>
                    <p className="text-gray-500 font-medium text-lg">Please complete the service requested by the customer.</p>
                </div>

                {/* Animated Loading Bar */}
                <div className="w-full max-w-md space-y-4">
                    <div className="flex justify-between text-[11px] text-gray-400 font-black uppercase tracking-[0.25em]">
                        <span>Started</span>
                        <span className="text-blue-600">Servicing</span>
                    </div>
                    <div className="h-4 bg-gray-200 rounded-full overflow-hidden relative shadow-inner">
                        <div className="absolute top-0 left-0 w-full h-full bg-blue-600 animate-wave rounded-full"></div>
                    </div>
                </div>

                {error && (
                    <div className="w-full max-w-md p-4 bg-red-50 text-red-700 rounded-xl text-sm font-bold border border-red-100 animate-bounce">
                        {error}
                    </div>
                )}

                {/* Payment Interaction Area */}
                <div className="w-full max-w-md space-y-6">

                    {showPaymentOptions && (
                        <div className="grid grid-cols-1 gap-3 animate-slide-up">
                            {paymentMethods.map((method) => (
                                <button
                                    key={method.id}
                                    onClick={() => {
                                        setSelectedPayment(method.id);
                                        setShowPaymentOptions(false);
                                    }}
                                    className={`w-full flex items-center justify-between p-4 rounded-xl border-2 transition-all ${selectedPayment === method.id
                                            ? 'border-blue-600 bg-white shadow-md'
                                            : 'border-white bg-white hover:border-gray-100'
                                        }`}
                                >
                                    <div className="flex items-center gap-4">
                                        <div
                                            className="w-10 h-10 rounded-lg flex items-center justify-center text-white shadow-sm"
                                            style={{ backgroundColor: method.color }}
                                        >
                                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2.5} stroke="currentColor" className="w-5 h-5">
                                                <path strokeLinecap="round" strokeLinejoin="round" d={method.icon} />
                                            </svg>
                                        </div>
                                        <span className="font-black text-gray-900 tracking-tight">{method.name}</span>
                                    </div>
                                    <div className={`w-6 h-6 rounded-full border-2 flex items-center justify-center transition-all ${selectedPayment === method.id
                                            ? 'border-blue-600 bg-blue-600'
                                            : 'border-gray-200'
                                        }`}>
                                        {selectedPayment === method.id && (
                                            <div className="w-2 h-2 rounded-full bg-white"></div>
                                        )}
                                    </div>
                                </button>
                            ))}
                        </div>
                    )}

                    <div className="space-y-4">
                        <button
                            onClick={() => setShowPaymentOptions(!showPaymentOptions)}
                            className={`w-full flex items-center justify-between p-5 rounded-2xl border-2 transition-all font-black text-sm uppercase tracking-widest ${showPaymentOptions
                                    ? 'border-blue-600 bg-blue-600 text-white shadow-lg shadow-blue-200'
                                    : 'border-white bg-white text-gray-900 shadow-sm hover:border-gray-100'
                                }`}
                        >
                            <span>{selectedPayment ? `Payment: ${paymentMethods.find(m => m.id === selectedPayment)?.name}` : 'Select Payment Method'}</span>
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={3} stroke="currentColor" className={`w-5 h-5 transition-transform ${showPaymentOptions ? 'rotate-180' : ''}`}>
                                <path strokeLinecap="round" strokeLinejoin="round" d="m19.5 8.25-7.5 7.5-7.5-7.5" />
                            </svg>
                        </button>

                        {selectedPayment && (
                            <button
                                onClick={handleCompleteService}
                                disabled={loading}
                                className="w-full flex items-center justify-center py-5 rounded-2xl text-lg font-black uppercase tracking-[0.2em] bg-blue-600 text-white shadow-xl shadow-blue-100 hover:bg-blue-700 transition-all transform active:scale-95 font-black"
                            >
                                {loading ? (
                                    <div className="flex items-center gap-3">
                                        <svg className="animate-spin h-6 w-6 text-white" viewBox="0 0 24 24">
                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"></circle>
                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        <span>Processing...</span>
                                    </div>
                                ) : 'Proceed to Pay'}
                            </button>
                        )}

                        <button
                            onClick={handleCompleteService}
                            disabled={loading || !selectedPayment}
                            className={`w-full flex items-center justify-center py-5 rounded-2xl text-lg font-black uppercase tracking-[0.2em] shadow-2xl transition-all transform active:scale-95 ${loading || !selectedPayment
                                    ? 'bg-gray-200 text-gray-400 cursor-not-allowed shadow-none'
                                    : 'bg-green-600 text-white hover:bg-green-700 shadow-green-200'
                                }`}
                        >
                            {loading ? (
                                <div className="flex items-center gap-3">
                                    <svg className="animate-spin h-6 w-6 text-white" viewBox="0 0 24 24">
                                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"></circle>
                                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                    </svg>
                                    <span>Updating...</span>
                                </div>
                            ) : (
                                'Mark Completed'
                            )}
                        </button>
                    </div>
                    {!selectedPayment && !showPaymentOptions && (
                        <p className="text-center text-[10px] text-gray-400 font-black uppercase tracking-widest mt-4 animate-pulse">
                            Please select payment to complete
                        </p>
                    )}
                </div>

            </div>
        </div>
    );
};

export default ServiceInProgress;
