import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { 
  ClipboardDocumentListIcon,
  CurrencyDollarIcon,
  CheckCircleIcon,
  BellIcon,
  CalendarIcon,
  MapPinIcon,
  ClockIcon,
  CheckIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { partnerService } from '../../services/partnerService';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalBookings: 0,
    completedBookings: 0,
    pendingBookings: 0,
    todayEarnings: 0,
    totalEarnings: 0,
    averageRating: 0
  });

  const [recentBookings, setRecentBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);
  const [error, setError] = useState(null);

  // Fetch partner data
  const fetchPartnerData = useCallback(async () => {
    try {
      setLoading(true);
      // Fetch stats and recent bookings in parallel
      const [statsData, bookingsData] = await Promise.all([
        partnerService.getStats(),
        partnerService.getBookings('pending,confirmed,in-progress')
      ]);
      
      setStats({
        totalBookings: statsData.totalBookings || 0,
        completedBookings: statsData.completedBookings || 0,
        pendingBookings: statsData.pendingBookings || 0,
        todayEarnings: statsData.todayEarnings || 0,
        totalEarnings: statsData.totalEarnings || 0,
        averageRating: statsData.averageRating || 0
      });

      setRecentBookings(bookingsData || []);
      setError(null);
    } catch (err) {
      console.error('Error fetching partner data:', err);
      setError('Failed to load data. Please try again later.');
      toast.error('Failed to load data. Please check your connection.');
    } finally {
      setLoading(false);
    }
  }, []);

  // Initial data fetch
  useEffect(() => {
    fetchPartnerData();
  }, [fetchPartnerData]);

  // Set up real-time updates (polling every 30 seconds)
  useEffect(() => {
    const interval = setInterval(() => {
      fetchPartnerData();
    }, 30000);

    return () => clearInterval(interval);
  }, [fetchPartnerData]);

  // Accept booking
  const handleAcceptBooking = async (bookingId) => {
    try {
      await partnerService.acceptBooking(bookingId);
      
      // Update local state optimistically
      setRecentBookings(prev => 
        prev.map(b => b._id === bookingId 
          ? { ...b, status: 'confirmed' } 
          : b
        )
      );
      
      setStats(prev => ({ 
        ...prev, 
        pendingBookings: Math.max(0, prev.pendingBookings - 1) 
      }));
      
      toast.success('Booking accepted successfully!');
      
      // Refresh data to ensure consistency
      fetchPartnerData();
    } catch (error) {
      console.error('Error accepting booking:', error);
      toast.error('Failed to accept booking. Please try again.');
    }
  };

  // Reject booking
  const handleRejectBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to reject this booking?')) {
      return;
    }

    try {
      await partnerService.rejectBooking(bookingId, 'Partner rejected the booking');
      
      // Update local state optimistically
      setRecentBookings(prev => prev.filter(b => b._id !== bookingId));
      setStats(prev => ({ 
        ...prev, 
        pendingBookings: Math.max(0, prev.pendingBookings - 1) 
      }));
      
      toast.info('Booking rejected');
      
      // Refresh data to ensure consistency
      fetchPartnerData();
    } catch (error) {
      console.error('Error rejecting booking:', error);
      toast.error('Failed to reject booking. Please try again.');
    }
  };

  // Color helpers
  const getUrgencyBadge = (level) => {
    const colors = {
      high: { bg: '#fee2e2', text: '#dc2626' },
      medium: { bg: '#fef3c7', text: '#d97706' },
      low: { bg: '#dcfce7', text: '#16a34a' }
    };
    return colors[level] || colors.low;
  };

  const statusColor = {
    pending: { bg: '#fef3c7', text: '#92400e' },
    confirmed: { bg: '#dbeafe', text: '#1e40af' },
    'in-progress': { bg: '#ede9fe', text: '#5b21b6' },
    completed: { bg: '#d1fae5', text: '#065f46' }
  };

  // Update booking status
  const updateBookingStatus = async (bookingId, status) => {
    try {
      await partnerService.updateBookingStatus(bookingId, status);
      
      // Update local state
      setRecentBookings(prev => 
        prev.map(b => b._id === bookingId 
          ? { ...b, status } 
          : b
        )
      );
      
      if (status === 'completed') {
        // Refresh stats when a booking is completed
        const statsData = await partnerService.getStats();
        setStats({
          totalBookings: statsData.totalBookings || 0,
          completedBookings: statsData.completedBookings || 0,
          pendingBookings: statsData.pendingBookings || 0,
          todayEarnings: statsData.todayEarnings || 0,
          totalEarnings: statsData.totalEarnings || 0,
          averageRating: statsData.averageRating || 0
        });
      }
      
      return true;
    } catch (error) {
      console.error('Error updating booking status:', error);
      toast.error('Failed to update booking status');
      return false;
    }
  };

  if (loading && recentBookings.length === 0) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-64">
        <div className="text-red-500 mb-4">{error}</div>
        <button 
          onClick={fetchPartnerData}
          className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6 bg-gray-100">
      
      {/* Header */}
      <div className="bg-white shadow rounded-lg p-6">
        <h1 className="text-3xl font-bold">Welcome back, Partner!</h1>
        <p className="text-gray-600 mt-2">Here's what's happening today</p>
      </div>

      {/* ---- Stats Grid ---- */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">

        {/* Total Bookings */}
        <div className="bg-white shadow rounded-lg p-6 flex items-center">
          <div className="p-3 bg-blue-100 rounded-full">
            <ClipboardDocumentListIcon className="h-6 w-6 text-blue-600" />
          </div>
          <div className="ml-4">
            <p className="text-sm text-gray-500">Total Bookings</p>
            <p className="text-2xl font-bold">{stats.totalBookings}</p>
          </div>
        </div>

        {/* Completed */}
        <div className="bg-white shadow rounded-lg p-6 flex items-center">
          <div className="p-3 bg-green-100 rounded-full">
            <CheckCircleIcon className="h-6 w-6 text-green-600" />
          </div>
          <div className="ml-4">
            <p className="text-sm text-gray-500">Completed</p>
            <p className="text-2xl font-bold">{stats.completedBookings}</p>
          </div>
        </div>

        {/* Today Earnings */}
        <div className="bg-white shadow rounded-lg p-6 flex items-center">
          <div className="p-3 bg-purple-100 rounded-full">
            <CurrencyDollarIcon className="h-6 w-6 text-purple-600" />
          </div>
          <div className="ml-4">
            <p className="text-sm text-gray-500">Today's Earnings</p>
            <p className="text-2xl font-bold">₹{stats.todayEarnings.toLocaleString()}</p>
          </div>
        </div>
      </div>

      {/* Recent Bookings */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold">Recent Bookings</h2>
          <Link to="/partner/bookings" className="text-blue-600 hover:underline">View All</Link>
        </div>
        
        <div className="space-y-4">
          {recentBookings.map(booking => (
            <div key={booking._id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="font-medium">{booking.service.name}</h3>
                  <p className="text-sm text-gray-600">{booking.customerName}</p>
                  <div className="flex items-center mt-1 text-sm text-gray-500">
                    <CalendarIcon className="h-4 w-4 mr-1" />
                    {new Date(booking.scheduledDate).toLocaleDateString()}
                  </div>
                  <div className="flex items-center mt-1 text-sm text-gray-500">
                    <ClockIcon className="h-4 w-4 mr-1" />
                    {new Date(booking.scheduledDate).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                  </div>
                  <div className="flex items-center mt-1 text-sm text-gray-500">
                    <MapPinIcon className="h-4 w-4 mr-1" />
                    {booking.address}
                  </div>
                </div>
                
                <div className="flex flex-col items-end">
                  <span 
                    className="px-2 py-1 rounded-full text-xs font-medium"
                    style={{
                      backgroundColor: statusColor[booking.status].bg,
                      color: statusColor[booking.status].text
                    }}
                  >
                    {booking.status.replace('-', ' ')}
                  </span>
                  <span className="mt-2 text-lg font-semibold">₹{booking.amount || booking.pricing?.totalAmount || 'N/A'}</span>
                </div>
              </div>
              
              <div className="mt-3 pt-3 border-t flex justify-between items-center">
                <div className="flex items-center">
                  <span 
                    className="px-2 py-1 rounded-full text-xs"
                    style={{
                      backgroundColor: getUrgencyBadge(booking.urgency).bg,
                      color: getUrgencyBadge(booking.urgency).text
                    }}
                  >
                    {booking.urgency} priority
                  </span>
                  {booking.duration && (
                    <span className="ml-2 text-sm text-gray-500">
                      <ClockIcon className="h-3 w-3 inline mr-1" />
                      {booking.duration}
                    </span>
                  )}
                </div>
                
                <div className="space-x-2">
                  {booking.status === 'pending' && (
                    <>
                      <button 
                        onClick={() => handleAcceptBooking(booking._id)}
                        className="px-3 py-1 bg-green-100 text-green-700 rounded-md text-sm hover:bg-green-200"
                      >
                        Accept
                      </button>
                      <button 
                        onClick={() => handleRejectBooking(booking._id)}
                        className="px-3 py-1 bg-red-100 text-red-700 rounded-md text-sm hover:bg-red-200"
                      >
                        Reject
                      </button>
                    </>
                  )}
                  {booking.status === 'in-progress' && (
                    <button 
                      onClick={async () => {
                        const success = await updateBookingStatus(booking._id, 'completed');
                        if (success) {
                          toast.success('Service marked as completed');
                        }
                      }}
                      className="px-3 py-1 bg-green-100 text-green-700 rounded-md text-sm hover:bg-green-200"
                    >
                      Mark as Completed
                    </button>
                  )}
                  {booking.status === 'confirmed' && (
                    <button 
                      onClick={async () => {
                        const success = await updateBookingStatus(booking._id, 'in-progress');
                        if (success) {
                          toast.info('Service started');
                        }
                      }}
                      className="px-3 py-1 bg-blue-100 text-blue-700 rounded-md text-sm hover:bg-blue-200"
                    >
                      Start Service
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      {/* Notifications */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Notifications</h2>
        <div className="space-y-3">
          {notifications.length > 0 ? (
            notifications.map((notif, index) => (
              <div 
                key={index} 
                className={`p-3 rounded-lg ${notif.type === 'new_booking' ? 'bg-blue-50' : notif.type === 'success' ? 'bg-green-50' : 'bg-yellow-50'}`}
              >
                <div className="flex justify-between">
                  <p className="text-sm">{notif.message}</p>
                  <span className="text-xs text-gray-500">
                    {new Date(notif.timestamp).toLocaleTimeString()}
                  </span>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-500 py-4">No new notifications</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
