import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import partnerService from '../../services/partnerService';
import Navbar from '../common/Navbar';
import bikeAnimation from '../../assets/bike_animation.mp4';

const ServiceInProgress = () => {
    const { bookingId } = useParams();
    const navigate = useNavigate();
    const [booking, setBooking] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [selectedPayment, setSelectedPayment] = useState(null);

    const canvasRef = React.useRef(null);
    const videoRef = React.useRef(null);
    // Removed gears refs as we are switching to SVG

    // Bike Animation Loop (Existing)
    useEffect(() => {
        const video = videoRef.current;
        const canvas = canvasRef.current;
        if (!video || !canvas) return;

        const ctx = canvas.getContext('2d', { willReadFrequently: true });
        let animationFrameId;

        const renderFrame = () => {
            if (video.paused || video.ended) return;
            if (canvas.width !== 320) { canvas.width = 320; canvas.height = 180; }

            ctx.save();
            ctx.scale(-1, 1);
            ctx.drawImage(video, -canvas.width, 0, canvas.width, canvas.height);
            ctx.restore();

            const frame = ctx.getImageData(0, 0, canvas.width, canvas.height);
            const data = frame.data;
            const l = data.length / 4;

            for (let i = 0; i < l; i++) {
                let r = data[i * 4 + 0];
                let g = data[i * 4 + 1];
                let b = data[i * 4 + 2];

                // Green Scan Removal
                if (g > 90 && g > r * 1.1 && g > b * 1.1) {
                    data[i * 4 + 3] = 0;
                    continue;
                }
                // Red to Blue
                if (r > g * 1.4 && r > b * 1.4 && r > 50) {
                    data[i * 4 + 0] = 37;
                    data[i * 4 + 1] = g + 50;
                    if (data[i * 4 + 1] > 255) data[i * 4 + 1] = 255;
                    data[i * 4 + 2] = r;
                }
            }
            ctx.putImageData(frame, 0, 0);
            animationFrameId = requestAnimationFrame(renderFrame);
        };
        const onPlay = () => renderFrame();
        video.addEventListener('play', onPlay);
        if (video.readyState >= 2) video.play().catch(e => console.log("Video play failed", e));
        else video.oncanplay = () => video.play().catch(e => console.log("Video play failed", e));

        return () => {
            cancelAnimationFrame(animationFrameId);
            video.removeEventListener('play', onPlay);
        };
    }, []);

    // Fetch booking details on mount and correct status if needed
    useEffect(() => {
        const fetchAndCorrectBooking = async () => {
            try {
                const response = await partnerService.getBookingById(bookingId);
                const fetchedBooking = response.data;
                setBooking(fetchedBooking);

                // If status is 'on_the_way', we must update to 'in_progress' because we are on the active service page
                if (fetchedBooking.status === 'on_the_way') {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    setBooking(prev => ({ ...prev, status: 'in_progress' }));
                }
            } catch (err) {
                console.error('Error fetching/correcting booking:', err);
                // Don't show error immediately, allow user to try "Mark Completed" which might work or fail with more info
            }
        };
        fetchAndCorrectBooking();
    }, [bookingId]);

    const handleCompleteService = async () => {
        try {
            setLoading(true);
            await partnerService.updateBookingStatus(bookingId, 'completed', {
                notes: `Payment received via ${selectedPayment.toUpperCase()}`
            });
            setLoading(false);
            navigate('/dashboard'); // Go back to dashboard after completion
        } catch (err) {
            console.error('Error completing service:', err);
            // Fallback: If error is restricted transition, try force updating to in_progress first
            if (err.response && err.response.status === 400 && booking?.status !== 'in_progress') {
                try {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    await partnerService.updateBookingStatus(bookingId, 'completed', {
                        notes: `Payment received via ${selectedPayment.toUpperCase()}`
                    });
                    setLoading(false);
                    navigate('/dashboard');
                    return;
                } catch (retryErr) {
                    console.error('Retry failed:', retryErr);
                }
            }

            setError('Failed to list service as completed. Please try again.');
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col">
            <Navbar />

            <div className="flex-grow flex flex-col items-center justify-center p-6 space-y-8">

                {/* Bike & Cogs Loop Animation */}
                <div className="relative w-full h-72 flex items-center justify-center overflow-hidden mb-8">
                    {/* 1. Bike Container - Drives Right & Disappears */}
                    <div className="absolute animate-drive-pass flex items-center justify-center">
                        {/* Smoke / Speed Trail */}
                        <div className="absolute left-0 top-20 opacity-60 z-0 transform rotate-6 scale-75">
                            <svg width="80" height="50" viewBox="0 0 100 60" fill="none" className="text-gray-400">
                                <path d="M100 30 C 80 40, 60 20, 40 30 C 30 35, 20 25, 10 30 C 5 32, 0 28, 0 35 L 0 45 C 10 40, 30 50, 50 40 C 70 30, 90 35, 100 30 Z" fill="url(#smokeGradient)" />
                                <defs>
                                    <linearGradient id="smokeGradient" x1="0" y1="0" x2="1" y2="0">
                                        <stop offset="0%" stopColor="currentColor" stopOpacity="0" />
                                        <stop offset="100%" stopColor="currentColor" stopOpacity="0.8" />
                                    </linearGradient>
                                </defs>
                            </svg>
                        </div>
                        {/* Hidden Video Source */}
                        <video
                            ref={videoRef}
                            src={bikeAnimation}
                            muted
                            loop
                            playsInline
                            className="hidden" // Hiding the raw video
                        />
                        {/* Canvas for processing */}
                        <canvas
                            ref={canvasRef}
                            className="w-48 h-32 object-contain drop-shadow-lg"
                        />
                    </div>

                    {/* 2. Cogs Container - Appears & Scales Up after bike */}
                    <div className="absolute animate-cogs-pop flex items-center justify-center">
                        {/* Increased internal container size to fit spread out gears */}
                        <div className="relative w-64 h-64 flex items-center justify-center">
                            {/* Metallic Gradient Definition */}
                            <svg width="0" height="0">
                                <defs>
                                    <linearGradient id="metalGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                                        <stop offset="0%" stopColor="#dbeafe" /> {/* light blue-100 */}
                                        <stop offset="50%" stopColor="#3b82f6" /> {/* blue-500 */}
                                        <stop offset="100%" stopColor="#1e40af" /> {/* blue-800 */}
                                    </linearGradient>
                                </defs>
                            </svg>

                            {/* Gear 1 (Large, Top Left) - Interlocked */}
                            <div className="absolute left-[calc(50%-78px)] top-[calc(50%-78px)] animate-spin" style={{ animationDuration: '4s' }}>
                                <svg width="80" height="80" viewBox="0 0 100 100" className="drop-shadow-md">
                                    <path fill="url(#metalGradient)" d="M50 0 L58 10 L70 5 L75 18 L88 15 L88 28 L98 32 L92 42 L100 50 L92 58 L98 68 L88 72 L88 85 L75 82 L70 95 L58 90 L50 100 L42 90 L30 95 L25 82 L12 85 L12 72 L2 68 L8 58 L0 50 L8 42 L2 32 L12 28 L12 15 L25 18 L30 5 L42 10 Z" />
                                    <circle cx="50" cy="50" r="15" fill="#f8fafc" />
                                    <circle cx="50" cy="50" r="8" fill="#94a3b8" />
                                </svg>
                            </div>

                            {/* Gear 2 (Small, Center) - Driving Gear */}
                            <div className="absolute left-[calc(50%-20px)] top-[calc(50%-20px)] z-10 animate-spin" style={{ animationDuration: '3s' }}>
                                <svg width="40" height="40" viewBox="0 0 100 100" className="drop-shadow-md">
                                    <path fill="url(#metalGradient)" d="M50 0 L58 10 L70 5 L75 18 L88 15 L88 28 L98 32 L92 42 L100 50 L92 58 L98 68 L88 72 L88 85 L75 82 L70 95 L58 90 L50 100 L42 90 L30 95 L25 82 L12 85 L12 72 L2 68 L8 58 L0 50 L8 42 L2 32 L12 28 L12 15 L25 18 L30 5 L42 10 Z" />
                                    <circle cx="50" cy="50" r="12" fill="#f8fafc" />
                                    <circle cx="50" cy="50" r="6" fill="#94a3b8" />
                                </svg>
                            </div>

                            {/* Gear 3 (Large, Bottom Right) - Interlocked */}
                            <div className="absolute left-[calc(50%-2px)] top-[calc(50%-2px)] animate-spin" style={{ animationDuration: '4s' }}>
                                <svg width="80" height="80" viewBox="0 0 100 100" className="drop-shadow-md">
                                    <path fill="url(#metalGradient)" d="M50 0 L58 10 L70 5 L75 18 L88 15 L88 28 L98 32 L92 42 L100 50 L92 58 L98 68 L88 72 L88 85 L75 82 L70 95 L58 90 L50 100 L42 90 L30 95 L25 82 L12 85 L12 72 L2 68 L8 58 L0 50 L8 42 L2 32 L12 28 L12 15 L25 18 L30 5 L42 10 Z" />
                                    <circle cx="50" cy="50" r="15" fill="#f8fafc" />
                                    <circle cx="50" cy="50" r="8" fill="#94a3b8" />
                                </svg>
                            </div>
                            <p className="absolute bottom-4 text-lg font-bold text-blue-600 animate-pulse text-center w-full z-20 whitespace-nowrap">Processing...</p>
                        </div>
                    </div>
                </div>

                <div className="text-center space-y-2">
                    <h2 className="text-2xl font-bold text-gray-900">Service in Progress</h2>
                    <p className="text-gray-500">Please complete the service requested by the customer.</p>
                </div>

                {/* Animated Loading Bar */}
                <div className="w-full max-w-md space-y-2">
                    <div className="flex justify-between text-xs text-gray-400 font-semibold uppercase tracking-wider">
                        <span>Started</span>
                        <span>SERVICING</span>
                    </div>
                    <div className="h-4 bg-gray-200 rounded-full overflow-hidden relative">
                        {/* Animated Striped Background */}
                        <div className="absolute top-0 left-0 w-full h-full bg-blue-600 animate-wave"></div>
                    </div>
                    <p className="text-center text-sm text-blue-600 font-medium animate-pulse">In Progress...</p>
                </div>

                {error && (
                    <div className="p-4 bg-red-50 text-red-700 rounded-md">
                        {error}
                    </div>
                )}

                {/* Payment Method Selection */}
                <div className="w-full max-w-md bg-white p-5 rounded-2xl border border-gray-100 shadow-sm space-y-4">
                    <div className="flex items-center justify-between">
                        <h3 className="text-sm font-black text-gray-400 uppercase tracking-widest">Select Payment Method</h3>
                        {selectedPayment && (
                            <span className="text-[10px] bg-green-100 text-green-700 px-2 py-0.5 rounded-full font-bold uppercase">
                                Selected
                            </span>
                        )}
                    </div>
                    <div className="grid grid-cols-2 gap-3">
                        {[
                            { id: 'cod', name: 'Cash', icon: 'M15 8.25V7.5a2.25 2.25 0 00-2.25-2.25h-1.5a2.25 2.25 0 00-2.25 2.25v.75m-3 0V15a2.25 2.25 0 002.25 2.25h9a2.25 2.25 0 002.25-2.25V8.25m-12 0h12M12 11.25v.75m0 2.25v.75' },
                            { id: 'online', name: 'Online', icon: 'M10.5 1.5H8.25A2.25 2.25 0 006 3.75v16.5a2.25 2.25 0 002.25 2.25h7.5A2.25 2.25 0 0018 20.25V3.75a2.25 2.25 0 00-2.25-2.25H13.5m-3 0V3h3V1.5m-3 0h3m-3 18.75h3' },
                            { id: 'card', name: 'Card', icon: 'M2.25 8.25h19.5M2.25 9h19.5m-16.5 5.25h6m-6 2.25h3m-3.75 3h15a2.25 2.25 0 002.25-2.25V6.75A2.25 2.25 0 0019.5 4.5h-15a2.25 2.25 0 00-2.25 2.25v10.5A2.25 2.25 0 004.5 19.5z' },
                            { id: 'wallet', name: 'Wallet', icon: 'M21 12a2.25 2.25 0 00-2.25-2.25H15a3 3 0 11-6 0H5.25A2.25 2.25 0 003 12m18 0v6a2.25 2.25 0 01-2.25 2.25H5.25A2.25 2.25 0 013 18v-6m18 0V9M3 12V9m18 0a2.25 2.25 0 00-2.25-2.25H5.25A2.25 2.25 0 003 9m18 0V6a2.25 2.25 0 00-2.25-2.25H5.25A2.25 2.25 0 003 6v3' }
                        ].map((method) => (
                            <button
                                key={method.id}
                                onClick={() => setSelectedPayment(method.id)}
                                className={`flex items-center gap-3 p-3 rounded-xl border-2 transition-all ${selectedPayment === method.id
                                    ? 'border-blue-600 bg-blue-50 text-blue-700 shadow-md transform scale-[1.02]'
                                    : 'border-gray-50 bg-gray-50 text-gray-500 hover:border-gray-200 hover:bg-white'
                                    }`}
                            >
                                <div className={`w-8 h-8 rounded-lg flex items-center justify-center ${selectedPayment === method.id ? 'bg-blue-600 text-white' : 'bg-white text-gray-400'
                                    }`}>
                                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-5 h-5">
                                        <path strokeLinecap="round" strokeLinejoin="round" d={method.icon} />
                                    </svg>
                                </div>
                                <span className="font-bold text-sm tracking-tight">{method.name}</span>
                            </button>
                        ))}
                    </div>
                </div>

                <div className="w-full max-w-md mt-2">
                    <button
                        onClick={handleCompleteService}
                        disabled={loading || !selectedPayment}
                        className={`w-full flex items-center justify-center px-6 py-4 border border-transparent text-lg font-black uppercase tracking-widest rounded-2xl text-white shadow-2xl transition-all transform active:scale-95 ${loading || !selectedPayment
                            ? 'bg-gray-400 cursor-not-allowed opacity-70'
                            : 'bg-green-600 hover:bg-green-700 shadow-green-600/20'
                            }`}
                    >
                        {loading ? (
                            <>
                                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                </svg>
                                Completing...
                            </>
                        ) : (
                            'Mark Service As Completed'
                        )}
                    </button>
                    {!selectedPayment && (
                        <p className="text-center text-[10px] text-gray-400 font-bold uppercase tracking-widest mt-3">
                            Please select a payment method to continue
                        </p>
                    )}
                </div>

            </div>
        </div>
    );
};

export default ServiceInProgress;
