import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import partnerService from '../../services/partnerService';
import Navbar from '../common/Navbar';
import bikeAnimation from '../../assets/bike_animation.mp4';
import gearsAnimation from '../../assets/gears_animation.mp4';

const ServiceInProgress = () => {
    const { bookingId } = useParams();
    const navigate = useNavigate();
    const [booking, setBooking] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const canvasRef = React.useRef(null);
    const videoRef = React.useRef(null);
    const gearsCanvasRef = React.useRef(null);
    const gearsVideoRef = React.useRef(null);

    // Bike Animation Loop (Existing)
    useEffect(() => {
        const video = videoRef.current;
        const canvas = canvasRef.current;
        if (!video || !canvas) return;

        const ctx = canvas.getContext('2d', { willReadFrequently: true });
        let animationFrameId;

        const renderFrame = () => {
            if (video.paused || video.ended) return;
            if (canvas.width !== 320) { canvas.width = 320; canvas.height = 180; }

            ctx.save();
            ctx.scale(-1, 1);
            ctx.drawImage(video, -canvas.width, 0, canvas.width, canvas.height);
            ctx.restore();

            const frame = ctx.getImageData(0, 0, canvas.width, canvas.height);
            const data = frame.data;
            const l = data.length / 4;

            for (let i = 0; i < l; i++) {
                let r = data[i * 4 + 0];
                let g = data[i * 4 + 1];
                let b = data[i * 4 + 2];

                // Green Scan Removal
                if (g > 90 && g > r * 1.1 && g > b * 1.1) {
                    data[i * 4 + 3] = 0;
                    continue;
                }
                // Red to Blue
                if (r > g * 1.4 && r > b * 1.4 && r > 50) {
                    data[i * 4 + 0] = 37;
                    data[i * 4 + 1] = g + 50;
                    if (data[i * 4 + 1] > 255) data[i * 4 + 1] = 255;
                    data[i * 4 + 2] = r;
                }
            }
            ctx.putImageData(frame, 0, 0);
            animationFrameId = requestAnimationFrame(renderFrame);
        };
        const onPlay = () => renderFrame();
        video.addEventListener('play', onPlay);
        if (video.readyState >= 2) video.play().catch(e => console.log("Video play failed", e));
        else video.oncanplay = () => video.play().catch(e => console.log("Video play failed", e));

        return () => {
            cancelAnimationFrame(animationFrameId);
            video.removeEventListener('play', onPlay);
        };
    }, []);

    // Gears Animation Loop (New)
    useEffect(() => {
        const video = gearsVideoRef.current;
        const canvas = gearsCanvasRef.current;
        if (!video || !canvas) return;

        const ctx = canvas.getContext('2d', { willReadFrequently: true });
        let animationFrameId;

        const renderFrame = () => {
            if (video.paused || video.ended) return;
            // Use smaller size for performance
            if (canvas.width !== 200) { canvas.width = 200; canvas.height = 200; }

            ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

            const frame = ctx.getImageData(0, 0, canvas.width, canvas.height);
            const data = frame.data;
            const l = data.length / 4;

            for (let i = 0; i < l; i++) {
                const r = data[i * 4 + 0];
                const g = data[i * 4 + 1];
                const b = data[i * 4 + 2];

                // 1. Remove White/Green Backgrounds (Simple Luma/Chroma Key)
                // Assuming bright white background or green screen
                if ((r > 200 && g > 200 && b > 200) || (g > r + 30 && g > b + 30)) {
                    data[i * 4 + 3] = 0; // Transparent
                    continue;
                }

                // 2. Tint greyscale metal to Blue (UI Color)
                // Calculate luminance
                const lum = 0.299 * r + 0.587 * g + 0.114 * b;

                // Apply Blue Tint: R=37, G=99, B=235
                // We map luminance to the target color intensity
                data[i * 4 + 0] = (lum / 255) * 37;
                data[i * 4 + 1] = (lum / 255) * 99;
                // Boost blue a bit more to make it pop
                data[i * 4 + 2] = (lum / 255) * 235;
            }

            ctx.putImageData(frame, 0, 0);
            animationFrameId = requestAnimationFrame(renderFrame);
        };

        const onPlay = () => renderFrame();
        video.addEventListener('play', onPlay);
        if (video.readyState >= 2) video.play().catch(e => console.log(e));
        else video.oncanplay = () => video.play().catch(e => console.log(e));

        return () => {
            cancelAnimationFrame(animationFrameId);
            video.removeEventListener('play', onPlay);
        };
    }, []);

    // Fetch booking details on mount and correct status if needed
    useEffect(() => {
        const fetchAndCorrectBooking = async () => {
            try {
                const response = await partnerService.getBookingById(bookingId);
                const fetchedBooking = response.data;
                setBooking(fetchedBooking);

                // If status is 'on_the_way', we must update to 'in_progress' because we are on the active service page
                if (fetchedBooking.status === 'on_the_way') {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    setBooking(prev => ({ ...prev, status: 'in_progress' }));
                }
            } catch (err) {
                console.error('Error fetching/correcting booking:', err);
                // Don't show error immediately, allow user to try "Mark Completed" which might work or fail with more info
            }
        };
        fetchAndCorrectBooking();
    }, [bookingId]);

    const handleCompleteService = async () => {
        try {
            setLoading(true);
            await partnerService.updateBookingStatus(bookingId, 'completed');
            setLoading(false);
            navigate('/dashboard'); // Go back to dashboard after completion
        } catch (err) {
            console.error('Error completing service:', err);
            // Fallback: If error is restricted transition, try force updating to in_progress first
            if (err.response && err.response.status === 400 && booking?.status !== 'in_progress') {
                try {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    await partnerService.updateBookingStatus(bookingId, 'completed');
                    setLoading(false);
                    navigate('/dashboard');
                    return;
                } catch (retryErr) {
                    console.error('Retry failed:', retryErr);
                }
            }

            setError('Failed to list service as completed. Please try again.');
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col">
            <Navbar />

            <div className="flex-grow flex flex-col items-center justify-center p-6 space-y-8">

                {/* Bike & Cogs Loop Animation */}
                <div className="relative w-full h-40 flex items-center justify-center overflow-hidden mb-6">
                    {/* 1. Bike Container - Drives Right & Disappears */}
                    <div className="absolute animate-drive-pass flex items-center justify-center">


                        {/* Hidden Video Source */}
                        <video
                            ref={videoRef}
                            src={bikeAnimation}
                            muted
                            loop
                            playsInline
                            className="hidden" // Hiding the raw video
                        />
                        {/* Canvas for processing */}
                        <canvas
                            ref={canvasRef}
                            className="w-48 h-32 object-contain drop-shadow-lg"
                        />
                    </div>

                    {/* 2. Cogs Container - Appears & Scales Up after bike */}
                    <div className="absolute animate-cogs-pop flex items-center justify-center">
                        {/* Hidden Video Source */}
                        <video
                            ref={gearsVideoRef}
                            src={gearsAnimation}
                            muted
                            loop
                            playsInline
                            className="hidden"
                        />
                        {/* Canvas for processing */}
                        <canvas
                            ref={gearsCanvasRef}
                            className="w-48 h-48 object-contain drop-shadow-lg"
                        />
                        <p className="absolute mt-32 text-sm font-semibold text-blue-500 animate-pulse">Processing...</p>
                    </div>
                </div>

                <div className="text-center space-y-2">
                    <h2 className="text-2xl font-bold text-gray-900">Service in Progress</h2>
                    <p className="text-gray-500">Please complete the service requested by the customer.</p>
                </div>

                {/* Animated Loading Bar */}
                <div className="w-full max-w-md space-y-2">
                    <div className="flex justify-between text-xs text-gray-400 font-semibold uppercase tracking-wider">
                        <span>Started</span>
                        <span>In Progress</span>
                    </div>
                    <div className="h-4 bg-gray-200 rounded-full overflow-hidden relative">
                        <div className="absolute top-0 left-0 bottom-0 bg-blue-600 w-full animate-progress-stripes bg-[length:40px_40px] bg-gradient-to-r from-blue-500 via-blue-600 to-blue-500"></div>
                        {/* CSS Animation for moving stripes would be ideal, simpler solid bar for now or keyframes in index.css */}
                        <div className="absolute top-0 left-0 h-full bg-blue-500 animate-[pulse_2s_cubic-bezier(0.4,0,0.6,1)_infinite] w-full origin-left"></div>
                    </div>
                    <p className="text-center text-sm text-blue-600 font-medium animate-pulse">Running...</p>
                </div>

                {error && (
                    <div className="p-4 bg-red-50 text-red-700 rounded-md">
                        {error}
                    </div>
                )}

                <div className="w-full max-w-md pt-8">
                    <button
                        onClick={handleCompleteService}
                        disabled={loading}
                        className="w-full flex items-center justify-center px-6 py-4 border border-transparent text-lg font-medium rounded-lg text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 shadow-xl transition-transform transform active:scale-95 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {loading ? (
                            <>
                                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                </svg>
                                Completing...
                            </>
                        ) : (
                            'Mark Service As Completed'
                        )}
                    </button>
                </div>

            </div>
        </div>
    );
};

