import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import partnerService from '../../services/partnerService';
import Navbar from '../common/Navbar';

const ServiceInProgress = () => {
    const { bookingId } = useParams();
    const navigate = useNavigate();
    const [booking, setBooking] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Fetch booking details on mount and correct status if needed
    useEffect(() => {
        const fetchAndCorrectBooking = async () => {
            try {
                const response = await partnerService.getBookingById(bookingId);
                const fetchedBooking = response.data;
                setBooking(fetchedBooking);

                // If status is 'on_the_way', we must update to 'in_progress' because we are on the active service page
                if (fetchedBooking.status === 'on_the_way') {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    setBooking(prev => ({ ...prev, status: 'in_progress' }));
                }
            } catch (err) {
                console.error('Error fetching/correcting booking:', err);
                // Don't show error immediately, allow user to try "Mark Completed" which might work or fail with more info
            }
        };
        fetchAndCorrectBooking();
    }, [bookingId]);

    const handleCompleteService = async () => {
        try {
            setLoading(true);
            await partnerService.updateBookingStatus(bookingId, 'completed');
            setLoading(false);
            navigate('/dashboard'); // Go back to dashboard after completion
        } catch (err) {
            console.error('Error completing service:', err);
            // Fallback: If error is restricted transition, try force updating to in_progress first
            if (err.response && err.response.status === 400 && booking?.status !== 'in_progress') {
                try {
                    await partnerService.updateBookingStatus(bookingId, 'in_progress');
                    await partnerService.updateBookingStatus(bookingId, 'completed');
                    setLoading(false);
                    navigate('/dashboard');
                    return;
                } catch (retryErr) {
                    console.error('Retry failed:', retryErr);
                }
            }

            setError('Failed to list service as completed. Please try again.');
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col">
            <Navbar />

            <div className="flex-grow flex flex-col items-center justify-center p-6 space-y-8">

                {/* Animated Icon/Illustration */}
                <div className="relative w-32 h-32 flex items-center justify-center">
                    <div className="absolute inset-0 bg-blue-100 rounded-full animate-ping opacity-75"></div>
                    <div className="relative bg-white p-4 rounded-full shadow-lg z-10">
                        <svg className="w-16 h-16 text-blue-600 animate-pulse" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.384-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 10.172V5L8 4z" />
                        </svg>
                    </div>
                </div>

                <div className="text-center space-y-2">
                    <h2 className="text-2xl font-bold text-gray-900">Service in Progress</h2>
                    <p className="text-gray-500">Please complete the service requested by the customer.</p>
                </div>

                {/* Animated Loading Bar */}
                <div className="w-full max-w-md space-y-2">
                    <div className="flex justify-between text-xs text-gray-400 font-semibold uppercase tracking-wider">
                        <span>Started</span>
                        <span>In Progress</span>
                    </div>
                    <div className="h-4 bg-gray-200 rounded-full overflow-hidden relative">
                        <div className="absolute top-0 left-0 bottom-0 bg-blue-600 w-full animate-progress-stripes bg-[length:40px_40px] bg-gradient-to-r from-blue-500 via-blue-600 to-blue-500"></div>
                        {/* CSS Animation for moving stripes would be ideal, simpler solid bar for now or keyframes in index.css */}
                        <div className="absolute top-0 left-0 h-full bg-blue-500 animate-[pulse_2s_cubic-bezier(0.4,0,0.6,1)_infinite] w-full origin-left"></div>
                    </div>
                    <p className="text-center text-sm text-blue-600 font-medium animate-pulse">Running...</p>
                </div>

                {error && (
                    <div className="p-4 bg-red-50 text-red-700 rounded-md">
                        {error}
                    </div>
                )}

                <div className="w-full max-w-md pt-8">
                    <button
                        onClick={handleCompleteService}
                        disabled={loading}
                        className="w-full flex items-center justify-center px-6 py-4 border border-transparent text-lg font-medium rounded-lg text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 shadow-xl transition-transform transform active:scale-95 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {loading ? (
                            <>
                                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                </svg>
                                Completing...
                            </>
                        ) : (
                            'Mark Service As Completed'
                        )}
                    </button>
                </div>

            </div>
        </div>
    );
};

export default ServiceInProgress;
