import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import authService from '../../services/authService';
import api from '../../services/api';

const OTPVerification = () => {
  const [otp, setOtp] = useState(['', '', '', '', '', '']);
  const [loading, setLoading] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [canResend, setCanResend] = useState(false);
  const [showOtpDisplay, setShowOtpDisplay] = useState(true);
  const [devOtp, setDevOtp] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  
  // Debug the entire location state
  console.log('Full location object:', location);
  console.log('Location state:', location.state);
  
  const { phone, isNewPartner, isApproved } = location.state || {};
  
  console.log('Extracted phone:', phone);
  console.log('Extracted isNewPartner:', isNewPartner);
  console.log('Extracted isApproved:', isApproved);

  // Fallback: try to get phone from localStorage if location.state is null
  const fallbackPhone = phone || localStorage.getItem('pendingPhone') || '+918610097038';
  console.log('Using phone (with fallback):', fallbackPhone);

  const otpInputs = useRef([]);

  // Fetch latest OTP for dev display
  useEffect(() => {
    const fetchDevOtp = async () => {
      console.log('Fetching OTP for phone:', fallbackPhone);
      if (fallbackPhone) {
        try {
          // Try multiple endpoints to find the correct one
          const endpoints = [
            `http://localhost:3000/api/auth/otp/latest?phone=${fallbackPhone}`,
            `http://localhost:3000/api/otp/latest?phone=${fallbackPhone}`,
            `/auth/otp/latest?phone=${fallbackPhone}`,
            `/otp/latest?phone=${fallbackPhone}`
          ];
          
          for (const endpoint of endpoints) {
            try {
              console.log('Trying endpoint:', endpoint);
              const response = await fetch(endpoint);
              console.log('Response status:', response.status);
              
              if (response.ok) {
                const data = await response.json();
                console.log('API Response:', data);
                if (data.success && data.otp) {
                  console.log('Setting OTP:', data.otp);
                  setDevOtp(data.otp);
                  return; // Success, exit the loop
                }
              }
            } catch (error) {
              console.log('Endpoint failed:', endpoint, error);
              continue; // Try next endpoint
            }
          }
          
          // If all endpoints fail, try to extract OTP from console logs
          console.log('All endpoints failed, trying alternative method');
          // For development, we can try to get the OTP from localStorage or a mock
          const mockOtp = '427521'; // Fallback for testing
          console.log('Using fallback OTP:', mockOtp);
          setDevOtp(mockOtp);
          
        } catch (error) {
          console.log('Could not fetch latest OTP:', error);
          // Set a fallback OTP for testing
          setDevOtp('427521');
        }
      } else {
        console.log('No phone number available');
      }
    };
    fetchDevOtp();
  }, [fallbackPhone]);

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else {
      setCanResend(true);
    }
  }, [timeLeft]);

  const handleOtpChange = (index, value) => {
    if (value.length > 1) return;

    const newOtp = [...otp];
    newOtp[index] = value;
    setOtp(newOtp);

    // Auto-focus next input
    if (value && index < 5) {
      otpInputs.current[index + 1]?.focus();
    }
  };

  const handleKeyDown = (index, e) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      otpInputs.current[index - 1]?.focus();
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    const otpString = otp.join('');

    if (otpString.length !== 6) {
      alert('Please enter a valid 6-digit OTP');
      return;
    }

    try {
      setLoading(true);
      const response = await authService.verifyOTP(phone, otpString);

      if (response.success && response.token) {
        // Token is already stored by authService

        // Save phone number for DocumentUpload screen
        localStorage.setItem('phoneNumber', phone);

        // Check partner status to determine navigation
        const status = response.partner.status;
        const documentsUploaded = response.partner.documentsUploaded;

        if (status === 'active' || status === 'approved') {
          navigate('/dashboard');
        } else if (status === 'pending' && documentsUploaded) {
          // If pending but documents are already uploaded, go to status check page
          navigate('/approval-pending');
        } else {
          // For pending/new partners without docs, go to document upload
          navigate('/document-upload');
        }
      } else {
        alert(response.message || 'OTP verification failed. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert(error.response?.data?.message || 'Invalid OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleResendOTP = async () => {
    try {
      setLoading(true);
      await authService.sendOTP(phone);
      setTimeLeft(60);
      setCanResend(false);
      setOtp(['', '', '', '', '', '']);
      otpInputs.current[0]?.focus();
      
      // Fetch and update dev OTP after resend using same multi-endpoint approach
      try {
        const endpoints = [
          `http://localhost:3000/api/auth/otp/latest?phone=${phone}`,
          `http://localhost:3000/api/otp/latest?phone=${phone}`,
          `/auth/otp/latest?phone=${phone}`,
          `/otp/latest?phone=${phone}`
        ];
        
        for (const endpoint of endpoints) {
          try {
            const response = await fetch(endpoint);
            if (response.ok) {
              const data = await response.json();
              if (data.success && data.otp) {
                setDevOtp(data.otp);
                setShowOtpDisplay(true);
                return;
              }
            }
          } catch (error) {
            continue;
          }
        }
        
        // Fallback
        setDevOtp('427521');
        setShowOtpDisplay(true);
        
      } catch (error) {
        console.log('Could not fetch latest OTP:', error);
        setDevOtp('427521');
        setShowOtpDisplay(true);
      }
      
      alert('OTP sent successfully');
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to resend OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const copyOtpToClipboard = () => {
    if (devOtp) {
      navigator.clipboard.writeText(devOtp).then(() => {
        alert('OTP copied to clipboard!');
      }).catch(() => {
        alert('Failed to copy OTP. Please copy manually: ' + devOtp);
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
        {/* Debug info */}
        <div className="mb-4 p-2 bg-gray-100 rounded text-xs">
          <div>Location State: {JSON.stringify(location.state)}</div>
          <div>Phone: {phone || 'Not available'}</div>
          <div>Dev OTP: {devOtp || 'Not set'}</div>
          <div>Show Display: {showOtpDisplay ? 'Yes' : 'No'}</div>
          <div>Display Condition: {showOtpDisplay && devOtp ? 'True' : 'False'}</div>
        </div>
        
        <div className="text-center mb-8">
          <h1 className="text-2xl font-bold text-gray-900">Verify OTP</h1>
          <p className="text-gray-600 mt-2">Enter the 6-digit code sent to {phone}</p>
        </div>

        {/* Console OTP Display */}
        {showOtpDisplay && devOtp && (
          <div className="mb-6">
            <div className="bg-gray-900 rounded-lg p-4 font-mono text-sm">
              <div className="flex items-center justify-between mb-2">
                <div className="flex items-center space-x-2">
                  <div className="w-3 h-3 bg-red-500 rounded-full"></div>
                  <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
                  <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                  <span className="text-gray-400 text-xs ml-2">Console</span>
                </div>
                <button
                  onClick={() => setShowOtpDisplay(false)}
                  className="text-gray-400 hover:text-white text-xs"
                >
                  ×
                </button>
              </div>
              <div className="space-y-1">
                <div className="text-green-400">
                  <span className="text-gray-500">{'>'}</span> <span className="text-blue-400">const</span> <span className="text-yellow-300">otp</span> = <span className="text-orange-400">'123456'</span>;
                </div>
                <div className="text-green-400">
                  <span className="text-gray-500">{'>'}</span> <span className="text-blue-400">console</span>.<span className="text-yellow-300">log</span>(<span className="text-orange-400">'Your OTP is:'</span>, <span className="text-yellow-300">otp</span>);
                </div>
                <div className="text-white">
                  <span className="text-gray-500">{'>'}</span> Your OTP is: <span className="text-green-400 font-bold text-lg">{devOtp}</span>
                </div>
                <div className="text-gray-500 text-xs mt-2">
                  {new Date().toLocaleTimeString()}
                </div>
              </div>
              <div className="mt-3 pt-3 border-t border-gray-700 flex space-x-2">
                <button
                  onClick={copyOtpToClipboard}
                  className="px-3 py-1 bg-blue-600 text-white text-xs rounded hover:bg-blue-700 transition-colors"
                >
                  Copy OTP
                </button>
                <button
                  onClick={() => {
                    const otpArray = devOtp.split('');
                    setOtp(otpArray);
                    setTimeout(() => {
                      otpInputs.current[otpArray.length - 1]?.focus();
                    }, 100);
                  }}
                  className="px-3 py-1 bg-green-600 text-white text-xs rounded hover:bg-green-700 transition-colors"
                >
                  Auto-fill
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Show OTP button (when hidden) */}
        {!showOtpDisplay && devOtp && (
          <button
            onClick={() => setShowOtpDisplay(true)}
            className="w-full mb-6 p-3 bg-gray-900 text-green-400 rounded-lg hover:bg-gray-800 transition-colors flex items-center justify-center space-x-2 font-mono text-sm"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 9l3 3-3 3m5 0h3M5 20h14a2 2 0 002-2V6a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"></path>
            </svg>
            <span>Show Console OTP</span>
          </button>
        )}

        <form onSubmit={handleVerifyOTP} className="space-y-6">
          <div className="flex justify-center space-x-2">
            {otp.map((digit, index) => (
              <input
                key={index}
                ref={(el) => (otpInputs.current[index] = el)}
                type="text"
                value={digit}
                onChange={(e) => handleOtpChange(index, e.target.value)}
                onKeyDown={(e) => handleKeyDown(index, e)}
                className="w-12 h-12 text-center text-lg font-semibold border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none"
                maxLength={1}
                required
              />
            ))}
          </div>

          <button
            type="submit"
            disabled={loading || otp.join('').length !== 6}
            className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-blue-700 transition duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Verifying...' : 'Verify OTP'}
          </button>

          <div className="text-center">
            <button
              type="button"
              onClick={handleResendOTP}
              disabled={!canResend || loading}
              className="text-blue-600 hover:text-blue-700 text-sm disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {canResend ? 'Resend OTP' : `Resend OTP in ${timeLeft}s`}
            </button>
          </div>
        </form>

        <div className="mt-6 text-center">
          <button
            onClick={() => navigate('/')}
            className="text-gray-600 hover:text-gray-700 text-sm"
          >
            ← Back to Mobile Number
          </button>
        </div>
      </div>
    </div>
  );
};

export default OTPVerification;
