import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api/partner';

const getAuthHeaders = () => ({
  'Authorization': `Bearer ${localStorage.getItem('token') || localStorage.getItem('authToken')}`,
  'Content-Type': 'application/json'
});

export const partnerService = {
  // Get partner's bookings
  getBookings: async (status = '') => {
    try {
      const response = await axios.get(
        `${API_BASE_URL}/bookings${status ? `?status=${status}` : ''}`,
        { headers: getAuthHeaders() }
      );
      return response.data;
    } catch (error) {
      console.error('Error fetching bookings:', error);
      throw error;
    }
  },

  // Accept a booking
  acceptBooking: async (bookingId) => {
    try {
      const response = await axios.post(
        `${API_BASE_URL}/bookings/${bookingId}/accept`,
        {},
        { headers: getAuthHeaders() }
      );
      return response.data;
    } catch (error) {
      console.error('Error accepting booking:', error);
      throw error;
    }
  },

  // Reject a booking
  rejectBooking: async (bookingId, reason = '') => {
    try {
      const response = await axios.post(
        `${API_BASE_URL}/bookings/${bookingId}/reject`,
        { reason },
        { headers: getAuthHeaders() }
      );
      return response.data;
    } catch (error) {
      console.error('Error rejecting booking:', error);
      throw error;
    }
  },

  // Update booking status
  updateBookingStatus: async (bookingId, status) => {
    try {
      const response = await axios.put(
        `${API_BASE_URL}/bookings/${bookingId}/status`,
        { status },
        { headers: getAuthHeaders() }
      );
      return response.data;
    } catch (error) {
      console.error('Error updating booking status:', error);
      throw error;
    }
  },

  // Get partner stats
  getStats: async () => {
    try {
      const response = await axios.get(
        `${API_BASE_URL}/stats`,
        { headers: getAuthHeaders() }
      );
      return response.data;
    } catch (error) {
      console.error('Error fetching partner stats:', error);
      throw error;
    }
  }
};

export default partnerService;
